import React, { createContext, useState, useEffect, useCallback } from 'react';
import axios from 'axios';

export const VendorContext = createContext();

export const VendorProvider = ({ vendorId, children }) => {
  const [vendor, setVendor] = useState(null); // Vendor profile data
  const [products, setProducts] = useState([]); // Vendor products
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  // Function to fetch vendor profile and products
const fetchVendorAndProducts = useCallback(async (id) => {
  setLoading(true);
  setError(null);

  try {
    // Fetch vendor and products using the same structure as getNearbyVendors
    const vendorResponse = await axios.get(`http://localhost:5001/api/vendors/${id}`, { withCredentials: true });
    const vendor = vendorResponse.data.data || {};

    // Fetch products for the vendor
    const vendorProducts = await axios.get(`http://localhost:5001/api/vendor/inventory/public/${id}`);
    const productsData = vendorProducts.data.data || [];

    // Normalize product data structure to ensure consistency
    const normalizeProduct = (vp, vendorId) => ({
      _id: vp.productId?._id || vp._id,
      name: vp.productId?.name || vp.name,
      brand: vp.productId?.brand || vp.brand,
      category: vp.productId?.category || vp.category,
      strain: vp.variation?.strain || vp.strain || "N/A",
      thcContent: vp.variation?.thcContent || vp.thcContent || null,
      price: vp.variation?.price || vp.price || 0,
      salePrice: vp.variation?.salePrice || vp.salePrice || null,
      stock: vp.variation?.stock || vp.stock || 0,
      image: vp.variation?.image || vp.image || "/placeholder-image.png",
      description: vp.productId?.description || vp.description || "",
      amount: vp.variation?.amount || vp.amount || "N/A",
      vendorId, // Include vendorId
    });

    const formattedProducts = productsData.map((vp) => normalizeProduct(vp, id));


    setVendor({
      ...vendor,
      products: formattedProducts, // Attach products to vendor for display
      currentStatus: vendorStatus,
    });

    setProducts(formattedProducts); // Separate products state for filtering
  } catch (err) {
    console.error("Failed to fetch vendor data:", err.message);
    setError("Failed to load vendor or products data.");
  } finally {
    setLoading(false);
  }
}, []);


  // Function to update vendor's minimum order
  const updateMinOrder = async (formData) => {
    try {
      const response = await axios.put(
        'http://localhost:5001/api/vendors/min-order',
        formData,
        { withCredentials: true }
      );

      if (response.data.success) {
        setVendor((prev) => ({ 
          ...prev, 
          minOrder: formData.minOrder,
          acceptedPayments: formData.acceptedPayments 
        }));
        alert('Settings updated successfully!');
      } else {
        alert(response.data.message || 'Failed to update settings.');
      }
    } catch (err) {
      console.error('Error updating settings:', err.message);
      alert('An error occurred while updating settings.');
    }
  };

  // Function to update business hours
  const updateBusinessHours = async (businessHours) => {
    try {
      const response = await axios.put(
        'http://localhost:5001/api/vendors/business-hours',
        { businessHours },
        { withCredentials: true }
      );

      if (response.data.success) {
        setVendor((prev) => ({ ...prev, businessHours }));
        alert('Business hours updated successfully!');
      } else {
        alert(response.data.message || 'Failed to update business hours.');
      }
    } catch (err) {
      console.error('Error updating business hours:', err.message);
      alert('An error occurred while updating business hours.');
    }
  };

  // Function to refresh vendor data after updates
  const refreshVendorData = async () => {
    if (vendorId) {
      await fetchVendorAndProducts(vendorId);
    }
  };

  useEffect(() => {
    if (vendorId) {
      fetchVendorAndProducts(vendorId);
    }
  }, [vendorId, fetchVendorAndProducts]);

  return (
    <VendorContext.Provider
      value={{
        vendor,
        products,
        loading,
        error,
        updateMinOrder,
        updateBusinessHours,
        refreshVendorData,
      }}
    >
      {children}
    </VendorContext.Provider>
  );
};
