import React, { useState } from 'react';
import { Form, Button, Card, Alert, Container } from 'react-bootstrap';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();
    const { login } = useAuth();

    const pageStyle = {
        backgroundColor: '#fafafa',
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        paddingTop: '20px',
        paddingBottom: '20px'
      };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setLoading(true);

        try {
            const response = await axios.post(
                'http://localhost:5001/api/auth/login',
                { email, password },
                { withCredentials: true }
            );

            if (response.data?.success) {
                const { vendorId } = response.data;
                login(vendorId);
                navigate('/vendor-dashboard');
            } else {
                throw new Error(response.data?.message || 'Unexpected response structure');
            }
        } catch (err) {
            console.error('Login error:', err);
            setError('Login failed. Please check your credentials.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div s>
            <Container className="mt-5 d-flex justify-content-center">
            <Card style={{ maxWidth: '400px' }} className="shadow p-4">
                <Card.Body>
                    <h3 className="text-center mb-4">Vendor Login</h3>
                    {error && <Alert variant="danger">{error}</Alert>}
                    <Form onSubmit={handleSubmit}>
                        <Form.Group controlId="email" className="mb-3">
                            <Form.Label>Email</Form.Label>
                            <Form.Control
                                type="email"
                                placeholder="Enter your email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                        </Form.Group>

                        <Form.Group controlId="password" className="mb-3">
                            <Form.Label>Password</Form.Label>
                            <Form.Control
                                type="password"
                                placeholder="Enter your password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </Form.Group>

                        <Button
                            variant="primary"
                            type="submit"
                            className="w-100"
                            disabled={loading}
                        >
                            {loading ? 'Logging in...' : 'Sign In'}
                        </Button>
                    </Form>

                    <div className="text-center mt-3">
                        <Link to="/reset-password-request" style={{ textDecoration: 'none', color: '#007bff' }}>
                            Forgot Password?
                        </Link>
                    </div>
                </Card.Body>
            </Card>
        </Container>
        </div>

    );
};

export default Login;
