import React, { createContext, useState, useEffect } from 'react';

export const VendorContext = createContext();

export const VendorProvider = ({ vendorId, children }) => {
  const [vendor, setVendor] = useState(null);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const BASE_URL = 'http://localhost:5001/api/vendors';

  // Fetch vendor details
  const fetchVendorAndProducts = async (id) => {
    setLoading(true);
    setError(null);

    try {
      const [vendorResponse, productsResponse] = await Promise.all([
        fetch(`${BASE_URL}/${id}`),
        fetch(`${BASE_URL}/inventory/public/${id}`), 
      ]);

      const vendorData = await vendorResponse.json();
      const productsData = await productsResponse.json();

      setVendor(vendorData.data || {});
      setProducts(productsData.data || []);
    } catch (err) {
      console.error('Error fetching vendor data:', err);
      setError('Failed to load vendor data.');
    } finally {
      setLoading(false);
    }
  };

  // Update minimum order
  const updateMinOrder = async (newMinOrder) => {
    try {
      const response = await fetch(`${BASE_URL}/min-order`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include', // Include cookies for authentication
        body: JSON.stringify({ minOrder: newMinOrder }),
      });

      const data = await response.json();

      if (response.ok) {
        // Update local vendor state with new minOrder
        setVendor((prevVendor) => ({
          ...prevVendor,
          minOrder: newMinOrder,
        }));
        alert('Minimum order updated successfully!');
      } else {
        console.error('Failed to update minimum order:', data.message);
        throw new Error(data.message || 'Failed to update minimum order');
      }
    } catch (err) {
      console.error('Error in updateMinOrder:', err.message);
      setError('Failed to update minimum order.');
    }
  };

  useEffect(() => {
    if (vendorId) {
      fetchVendorAndProducts(vendorId);
    }
  }, [vendorId]);

  return (
    <VendorContext.Provider value={{ vendor, products, loading, error, updateMinOrder }}>
      {children}
    </VendorContext.Provider>
  );
};
