import React from 'react';
import { useNavigate } from 'react-router-dom'; // For navigation

const DeliveryOrPickupConfirmation = ({ serviceType, userAddress, vendorLocation }) => {
  const navigate = useNavigate();

  // Function to handle changing the service type
  const handleChangeServiceType = () => {
    // Redirect to the vendor's page where the toggle switch exists
    navigate('/vendor'); // Adjust this route to the correct path for the vendor's page
  };

  return (
    <div className="confirmation-container p-3 bg-light mb-1" style={{ borderRadius: '8px', margin: '20px' }}>
      <h5 className="mb-2">
        {/* Conditional heading based on service type */}
        {serviceType === 'delivery' ? 'Deliver to' : 'Pickup at 10401 Wilshire Blvd.'}
      </h5>

      {/* Conditional address based on service type */}
      <p className="mb-1">
        {serviceType === 'delivery' ? userAddress : vendorLocation}
      </p>

      {/* Change link */}
      <small>
        <a href="#" onClick={handleChangeServiceType}>
          Change
        </a>
      </small>
    </div>
  );
};

export default DeliveryOrPickupConfirmation;
