import React, { useState, useRef, useEffect } from 'react';
import { Offcanvas, Form, Button } from 'react-bootstrap';

const AddressInput = ({ onAddressUpdate }) => {
  const [show, setShow] = useState(false);
  const [selectedAddress, setSelectedAddress] = useState(null);
  const inputRef = useRef(null);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  useEffect(() => {
    // Initialize Google Places Autocomplete
    const initializeAutocomplete = () => {
      if (window.google && inputRef.current) {
        const autocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
          types: ['geocode'], // Suggest addresses only
          componentRestrictions: { country: 'us' }, // Restrict results to the US
        });

        // Listen for when a place is selected
        autocomplete.addListener('place_changed', () => {
          const place = autocomplete.getPlace();
          if (place.geometry) {
            const location = {
              lat: place.geometry.location.lat(),
              lng: place.geometry.location.lng(),
              address: place.formatted_address,
            };

            setSelectedAddress(location.address);
            onAddressUpdate(location); // Pass coordinates and address to parent
            handleClose(); // Close the Offcanvas after selection
          }
        });
      }
    };

    // Ensure script is loaded and initialize
    initializeAutocomplete();
  }, []);

  return (
    <>
      {/* Main Display Under Navbar */}
      <div className="address-bar d-flex align-items-center">
        <i className="bi bi-geo-alt-fill me-2"></i>
        <span
          className="address-link"
          onClick={handleShow}
          style={{ textDecoration: 'underline', cursor: 'pointer' }}
        >
          {selectedAddress ? `Deliver to ${selectedAddress}` : 'Enter your address'}
        </span>
      </div>

      {/* Slide-In Window */}
      <Offcanvas show={show} onHide={handleClose} placement="start">
        <Offcanvas.Header closeButton>
          <Offcanvas.Title>Enter your address</Offcanvas.Title>
        </Offcanvas.Header>
        <Offcanvas.Body>
          <Form.Group controlId="addressInput">
            <Form.Control
              ref={inputRef}
              type="text"
              placeholder="Enter your address"
              aria-label="Enter your address"
            />
          </Form.Group>
          <Button variant="secondary" onClick={handleClose} className="mt-3">
            Cancel
          </Button>
        </Offcanvas.Body>
      </Offcanvas>
    </>
  );
};

export default AddressInput;
