import React, { useState, useEffect, useCallback } from 'react';
import { Tabs, Tab, Button, Col, Row } from 'react-bootstrap';
import RecentOrders from './RecentOrders';
import InventoryComponent from './InventoryComponent';
import LogoutButton from './LogoutButton';
import UpdateDeliveryZoneComponent from './UpdateDeliveryZoneComponent';
import BusinessHoursComponent from './BusinessHoursComponent';
import MinOrderComponent from './MinOrderComponent';
import { useLoadScript } from '@react-google-maps/api';
import { useAuth } from '../context/AuthContext'; // Use useAuth to access context
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import VendorLogoCard from './VendorLogoCard';
import VendorInfoCard from './VendorInfoCard';
import DeactivateAccount from './DeactivateAccount';
import VendorProfileCard from './VendorProfileCard';

const libraries = ['places'];

const defaultHours = {
  monday: '',
  tuesday: '',
  wednesday: '',
  thursday: '',
  friday: '',
  saturday: '',
  sunday: '',
};

const generateTimeSlots = () => {
  const slots = [];
  let time = 6 * 60; // Start at 6:00 AM in minutes
  const end = 24 * 60; // End at midnight (24:00)

  while (time < end) {
    const hours = Math.floor(time / 60); // Convert minutes to hours
    const label = `${hours % 12 || 12}:00 ${hours < 12 ? 'AM' : 'PM'}`; // Format as AM/PM
    slots.push(label);
    time += 60; // Increment by 60 minutes for hourly slots
  }
  return slots;
};

const VendorDashboard = () => {
  const { logout } = useAuth(); // Removed any token-related functions
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries,
  });
  const navigate = useNavigate();

  const [initialNotice, setInitialNotice] = useState('');
  const [initialMinOrder, setInitialMinOrder] = useState(0);
  const [initialHours, setInitialHours] = useState(defaultHours); // Using defaultHours
  const [showSettings, setShowSettings] = useState(false); // State for showing the sidebar
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null); // To manage error states



  const handleExpiredToken = () => {
    console.warn('Session expired. Logging out.');
    logout();
    navigate('/login'); // Redirect to login
  };

  const fetchVendorData = useCallback(async () => {
    if (loading) return;
    setLoading(true); // Start loading
    try {
      // Make API call to fetch vendor profile using cookies
      const response = await axios.get('http://localhost:5001/api/vendors/profile', {
        withCredentials: true, // Include cookies in the request
      });

      const { data } = response.data; // Access the `data` object in the response
      setInitialNotice(data.storeNotice || '');
      setInitialMinOrder(data.minOrder || 0);
      setInitialHours(data.businessHours || defaultHours);

      setError(null); // Clear any previous errors
    } catch (error) {
      console.error('Error fetching vendor data:', error.message);

      if (error.response?.status === 401) {
        handleExpiredToken();
      } else {
        setError('Failed to fetch vendor data.');
      }
    } finally {
      setLoading(false); // Stop loading
    }
  }, [handleExpiredToken]);

  useEffect(() => {
    fetchVendorData(); // Fetch data on mount
  }, []); // Empty dependency array ensures it only runs once


  const handleUpdateMinOrder = async (newMinOrder) => {
    try {
      const response = await axios.put(
        'http://localhost:5001/api/vendors/min-order', // API route
        { minOrder: newMinOrder },                    // Payload
        { withCredentials: true }                    // Include cookies for authentication
      );

      if (response.data.success) {
        alert('Minimum order updated successfully!');
        setInitialMinOrder(newMinOrder); // Update the local state
      } else {
        alert(response.data.message || 'Failed to update minimum order.');
      }
    } catch (error) {
      console.error('Error updating minimum order:', error);
      alert('An error occurred while updating the minimum order. Please try again.');
    }
  };



  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading map...</div>;

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center m-4">
        <h2 className="flex-grow-1 text-center">Vendor Dashboard</h2>
        <LogoutButton />
      </div>
      {loading && <div>Loading...</div>}
      {error && <div className="alert alert-danger">{error}</div>}
      <Tabs defaultActiveKey="orders" id="vendor-dashboard-tabs" className="mb-3">
        <Tab eventKey="orders" title="Orders">
          <RecentOrders />
        </Tab>
        <Tab eventKey="inventory" title="Inventory">
          <InventoryComponent />
        </Tab>
        <Tab eventKey="profile" title="Profile">
  <div className="p-4">
    {/* Top Section: Vendor Logo and Info */}
    <div className="mb-4">
      <Row className="g-4">
        <Col xs={12} md={4}>
          <VendorLogoCard />
        </Col>
        <Col xs={12} md={8}>
          <VendorInfoCard />
        </Col>
      </Row>
    </div>

    {/* Middle Section: Min Order and Update Delivery Zone */}
    <div className="mb-4">
      <Row className="g-4">
      <Col xs={12} md={6}>
          <UpdateDeliveryZoneComponent isLoaded={isLoaded} loadError={loadError} />
        </Col>
        <Col xs={12} md={6}>
          <MinOrderComponent
            minOrder={initialMinOrder} // Pass the current minimum order
            onUpdate={handleUpdateMinOrder} // Pass the update function
          />
        </Col>
      </Row>
    </div>
    <VendorProfileCard />

    {/* Bottom Section: Business Hours */}
    <div>
      <BusinessHoursComponent
        initialHours={initialHours} // Initial business hours fetched from backend
        onUpdate={async (updatedHours) => {
          try {
            // Send updated hours to the backend API
            const response = await axios.put(
              'http://localhost:5001/api/vendors/business-hours',
              { businessHours: updatedHours },
              { withCredentials: true } // Include cookies or tokens for authentication
            );

            if (response.data.success) {
              alert('Business hours updated successfully!');
              setInitialHours(updatedHours); // Update the local state with the new hours
            } else {
              alert(response.data.message || 'Failed to update business hours.');
            }
          } catch (error) {
            console.error('Error updating business hours:', error);
            alert('An error occurred while updating business hours. Please try again.');
          }
        }}
        availableTimes={generateTimeSlots()} // Dynamically generate time slots
      />
    </div>
    {/* Top Section: Vendor Logo and Info */}
    <div className="mb-4">
      <Row className="g-4">
        <Col xs={12} md={4}>
          <VendorLogoCard />
        </Col>
        <Col xs={12} md={8}>
          <VendorInfoCard />
        </Col>
      </Row>
    </div>
  </div>
</Tab>

      </Tabs>

    </div>
  );
};

export default VendorDashboard;
