import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Row, Col, Form, Card, Container } from 'react-bootstrap';
import { CartContext } from '../context/CartContext';

const TestPlaceOrder = () => {
  const { calculateTotal, cartItems, clearCart } = useContext(CartContext);

  const [contactInfo, setContactInfo] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
  });

  const [savedAddress, setSavedAddress] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [newAddress, setNewAddress] = useState('');

  const navigate = useNavigate();

  const subtotal = calculateTotal();
  const taxRate = 0.17;
  const estimatedTotal = subtotal + subtotal * taxRate;

  useEffect(() => {
    const address = localStorage.getItem('userAddress');
    if (address) {
      setSavedAddress(address);
    }
  }, []);

  const handleSaveAddress = () => {
    localStorage.setItem('userAddress', newAddress);
    setSavedAddress(newAddress);
    setIsEditing(false);
  };

  const handlePlaceOrder = async () => {
    if (!contactInfo.firstName || !contactInfo.lastName || !contactInfo.email || !contactInfo.phone) {
      alert('Please fill out all required fields.');
      return;
    }
    if (!savedAddress) {
      alert('Please add a delivery address.');
      return;
    }

    // Payload and fetch logic here...
    clearCart();
    navigate('/order-confirmation');
  };

  return (
    <div>
      <div>
    <Container fluid className="place-order-section py-4">
    <Row className="g-4">
      {/* Left Container: Delivery Address and Contact Information */}
      <Col lg={6} xs={12}>
        <div className="p-4" style={{ backgroundColor: '#f8f9fa' }}>
          <Card>
            <Card.Body>
              <h4>Delivery Address</h4>
              {isEditing ? (
                <Form>
                  <Form.Group controlId="address" className="mb-3">
                    <Form.Label>Delivery Address</Form.Label>
                    <Form.Control
                      type="text"
                      value={newAddress}
                      onChange={(e) => setNewAddress(e.target.value)}
                      placeholder="Enter your address"
                      required
                    />
                  </Form.Group>
                  <Button variant="success" onClick={handleSaveAddress}>
                    Save Address
                  </Button>
                </Form>
              ) : (
                <div>
                  <Card.Text>
                    <strong>Address:</strong> {savedAddress || 'N/A'}
                  </Card.Text>
                  <Button
                    variant="primary"
                    onClick={() => {
                      setNewAddress(savedAddress);
                      setIsEditing(true);
                    }}
                  >
                    Edit
                  </Button>
                </div>
              )}
              <hr />
              <h4>Contact Information</h4>
              <Form>
                <Row className="mb-3">
                  <Col>
                    <Form.Label>First Name</Form.Label>
                    <Form.Control
                      type="text"
                      value={contactInfo.firstName}
                      onChange={(e) => setContactInfo({ ...contactInfo, firstName: e.target.value })}
                      required
                    />
                  </Col>
                  <Col>
                    <Form.Label>Last Name</Form.Label>
                    <Form.Control
                      type="text"
                      value={contactInfo.lastName}
                      onChange={(e) => setContactInfo({ ...contactInfo, lastName: e.target.value })}
                      required
                    />
                  </Col>
                </Row>
                <Row className="mb-3">
                  <Col>
                    <Form.Label>Email</Form.Label>
                    <Form.Control
                      type="email"
                      value={contactInfo.email}
                      onChange={(e) => setContactInfo({ ...contactInfo, email: e.target.value })}
                      required
                    />
                  </Col>
                  <Col>
                    <Form.Label>Phone</Form.Label>
                    <Form.Control
                      type="text"
                      value={contactInfo.phone}
                      onChange={(e) => setContactInfo({ ...contactInfo, phone: e.target.value })}
                      required
                    />
                  </Col>
                </Row>
              </Form>
            </Card.Body>
          </Card>
        </div>
      </Col>

      {/* Right Container: Order Summary and Place Order */}
      <Col lg={6} xs={12}>
        <div className="p-4" style={{ backgroundColor: '#e9ecef' }}>
          <Card>
            <Card.Body>
              <h4>Order Summary</h4>
              {cartItems.length > 0 ? (
                cartItems.map((item) => (
                  <div key={item.productId} className="d-flex justify-content-between align-items-center mb-3">
                    <div>{item.name}</div>
                    <div>{item.quantity} x ${item.price.toFixed(2)}</div>
                  </div>
                ))
              ) : (
                <p>Your cart is empty.</p>
              )}
              <hr />
              <div className="d-flex justify-content-between">
                <strong>Total</strong>
                <span>${estimatedTotal.toFixed(2)}</span>
              </div>
              <Button
                variant="primary"
                className="w-100 mt-3"
                onClick={handlePlaceOrder}
              >
                Place Order
              </Button>
            </Card.Body>
          </Card>
        </div>
      </Col>
    </Row>
  </Container>
  </div>
    <div>
      <Container fluid className="place-order-section py-4">
      <Row className="g-4">
        {/* Left Container: Delivery Address and Contact Information */}
        <Col lg={6} xs={12}>
          <div className="p-4" style={{ backgroundColor: '#f8f9fa' }}>
            <Card>
              <Card.Body>
                <h4>Delivery Address</h4>
                {isEditing ? (
                  <Form>
                    <Form.Group controlId="address" className="mb-3">
                      <Form.Label>Delivery Address</Form.Label>
                      <Form.Control
                        type="text"
                        value={newAddress}
                        onChange={(e) => setNewAddress(e.target.value)}
                        placeholder="Enter your address"
                        required
                      />
                    </Form.Group>
                    <Button variant="success" onClick={handleSaveAddress}>
                      Save Address
                    </Button>
                  </Form>
                ) : (
                  <div>
                    <Card.Text>
                      <strong>Address:</strong> {savedAddress || 'N/A'}
                    </Card.Text>
                    <Button
                      variant="primary"
                      onClick={() => {
                        setNewAddress(savedAddress);
                        setIsEditing(true);
                      }}
                    >
                      Edit
                    </Button>
                  </div>
                )}
                <hr />
                <h4>Contact Information</h4>
                <Form>
                  <Row className="mb-3">
                    <Col>
                      <Form.Label>First Name</Form.Label>
                      <Form.Control
                        type="text"
                        value={contactInfo.firstName}
                        onChange={(e) => setContactInfo({ ...contactInfo, firstName: e.target.value })}
                        required
                      />
                    </Col>
                    <Col>
                      <Form.Label>Last Name</Form.Label>
                      <Form.Control
                        type="text"
                        value={contactInfo.lastName}
                        onChange={(e) => setContactInfo({ ...contactInfo, lastName: e.target.value })}
                        required
                      />
                    </Col>
                  </Row>
                  <Row className="mb-3">
                    <Col>
                      <Form.Label>Email</Form.Label>
                      <Form.Control
                        type="email"
                        value={contactInfo.email}
                        onChange={(e) => setContactInfo({ ...contactInfo, email: e.target.value })}
                        required
                      />
                    </Col>
                    <Col>
                      <Form.Label>Phone</Form.Label>
                      <Form.Control
                        type="text"
                        value={contactInfo.phone}
                        onChange={(e) => setContactInfo({ ...contactInfo, phone: e.target.value })}
                        required
                      />
                    </Col>
                  </Row>
                </Form>
              </Card.Body>
            </Card>
          </div>
        </Col>

        {/* Right Container: Order Summary and Place Order */}
        <Col lg={6} xs={12}>
          <div className="p-4" style={{ backgroundColor: '#e9ecef' }}>
            <Card>
              <Card.Body>
                <h4>Order Summary</h4>
                {cartItems.length > 0 ? (
                  cartItems.map((item) => (
                    <div key={item.productId} className="d-flex justify-content-between align-items-center mb-3">
                      <div>{item.name}</div>
                      <div>{item.quantity} x ${item.price.toFixed(2)}</div>
                    </div>
                  ))
                ) : (
                  <p>Your cart is empty.</p>
                )}
                <hr />
                <div className="d-flex justify-content-between">
                  <strong>Total</strong>
                  <span>${estimatedTotal.toFixed(2)}</span>
                </div>
                <Button
                  variant="primary"
                  className="w-100 mt-3"
                  onClick={handlePlaceOrder}
                >
                  Place Order
                </Button>
              </Card.Body>
            </Card>
          </div>
        </Col>
      </Row>
    </Container>
    </div>

    </div>

  );
};

export default TestPlaceOrder;
