import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';

const VendorMapNew = ({ selectedVendorId, onVendorSelect }) => {
  const [vendors, setVendors] = useState([]);
  const [selectedVendor, setSelectedVendor] = useState(null);
  const [mapCenter, setMapCenter] = useState({
    lat: 34.0522,
    lng: -118.2437
  });

  const mapStyles = {
    height: "70vh",
    width: "100%"
  };

  const defaultOptions = {
    panControl: true,
    zoomControl: true,
    mapTypeControl: true,
    scaleControl: true,
    streetViewControl: true,
    rotateControl: true,
    clickableIcons: true,
    keyboardShortcuts: true,
    scrollwheel: true,
    disableDoubleClickZoom: false,
    fullscreenControl: true,
  };

  useEffect(() => {
    const fetchVendors = async () => {
      try {
        const response = await fetch('/api/vendors/nearby');
        const data = await response.json();

        if (data.success && data.vendors) {
          const formattedVendors = data.vendors.map(vendor => ({
            ...vendor,
            coordinates: vendor.storefrontAddress?.coordinates ? {
              lat: vendor.storefrontAddress.coordinates[1],
              lng: vendor.storefrontAddress.coordinates[0]
            } : null
          })).filter(vendor => vendor.coordinates);

          setVendors(formattedVendors);
        }
      } catch (error) {
        console.error('Error fetching vendors:', error);
      }
    };

    fetchVendors();
  }, []);

  // Update selectedVendor when selectedVendorId changes
  useEffect(() => {
    if (selectedVendorId) {
      const vendor = vendors.find(v => v._id === selectedVendorId);
      setSelectedVendor(vendor);

      // Optional: Center map on selected vendor
      if (vendor?.coordinates) {
        setMapCenter(vendor.coordinates);
      }
    } else {
      setSelectedVendor(null);
    }
  }, [selectedVendorId, vendors]);

  const handleMarkerClick = (vendor) => {
    onVendorSelect(vendor._id);
  };

  return (
    <div className="w-full">
      <div className="p-4 bg-white rounded-lg shadow-md">
        <h2 className="text-2xl font-semibold mb-4">Vendor Locations</h2>
        <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}>
          <GoogleMap
            mapContainerStyle={mapStyles}
            zoom={11}
            center={mapCenter}
            options={defaultOptions}
          >
            {vendors.map(vendor => (
              vendor.coordinates && (
                <Marker
                  key={vendor._id}
                  position={vendor.coordinates}
                  onClick={() => handleMarkerClick(vendor)}
                  icon={{
                    url: vendor.logoUrl || 'default-marker.png',
                    scaledSize: { width: 40, height: 40 }
                  }}
                />
              )
            ))}

            {selectedVendor && (
              <InfoWindow
                position={selectedVendor.coordinates}
                onCloseClick={() => onVendorSelect(null)}
              >
                <div className="p-2">
                  <h3 className="font-semibold text-lg">{selectedVendor.dispensaryName}</h3>
                  <p className="text-sm text-gray-600">{selectedVendor.storefrontAddress.formatted}</p>
                  <p className="text-sm mt-1">
                    {selectedVendor.dispensaryType}
                  </p>
                  {selectedVendor.minOrder && (
                    <p className="text-sm">
                      Min. Order: ${selectedVendor.minOrder}
                    </p>
                  )}
                </div>
              </InfoWindow>
            )}
          </GoogleMap>
        </LoadScript>
      </div>
    </div>
  );
};

export default VendorMapNew;
