import React, { useState } from 'react';
import { Button, Modal, Alert } from 'react-bootstrap';
import axios from 'axios';

const DeactivateAccount = ({ onLogout }) => {
    const [showModal, setShowModal] = useState(false);
    const [alert, setAlert] = useState({ message: '', variant: '' });

    // Handle account deactivation
    const handleDeactivate = async () => {
        try {
            const response = await axios.put(
                '/api/vendors/deactivate-account',
                {}, // No body required
                {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('token')}`,
                    },
                }
            );
            setAlert({ message: response.data.message, variant: 'success' });
            onLogout(); // Log out the user after deactivating
        } catch (error) {
            console.error('Error deactivating account:', error);
            setAlert({ message: 'Failed to deactivate account. Please try again.', variant: 'danger' });
        } finally {
            setShowModal(false);
        }
    };

    return (
        <div>
            {/* Alert Message */}
            {alert.message && <Alert variant={alert.variant}>{alert.message}</Alert>}

            {/* Deactivate Account Button */}
            <Button variant="danger" onClick={() => setShowModal(true)}>
                Deactivate Account
            </Button>

            {/* Confirmation Modal */}
            <Modal show={showModal} onHide={() => setShowModal(false)} centered>
                <Modal.Header closeButton>
                    <Modal.Title>Confirm Deactivation</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    Are you sure you want to deactivate your account? You will need to register again to reactivate it.
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowModal(false)}>
                        Cancel
                    </Button>
                    <Button variant="danger" onClick={handleDeactivate}>
                        Confirm Deactivate
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default DeactivateAccount;
