import React, { useState } from 'react';
import { Form, Button, Card, Alert } from 'react-bootstrap';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import api from '../api/api'; // Assuming api.js is used for Axios configuration
import VendorRegistration from './VendorRegistration'; // Import the registration modal

const Login = () => {
    const { login } = useAuth(); // Use login function from AuthContext
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [showRegisterModal, setShowRegisterModal] = useState(false); // Modal visibility state
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        try {
            // Send login request to the backend
            const response = await api.post('/auth/login', { email, password });

            console.log("Response Data:", response.data); // Debug response

            // Update auth context with tokens and vendorId
            login({
                token: response.data.accessToken,
                refreshToken: response.data.refreshToken,
                vendorId: response.data.vendorId,
            });

            // Redirect to vendor dashboard
            navigate('/vendor-dashboard');
        } catch (error) {
            console.error("Error during login:", error.response?.data);
            setError('Invalid credentials. Please try again.');
        }
    };

    return (
        <>
            {/* Vendor Registration Modal */}
            {showRegisterModal && (
                <VendorRegistration
                    show={showRegisterModal}
                    handleClose={() => setShowRegisterModal(false)}
                />
            )}

            {/* Login Form */}
            <div className="container mt-5" style={{ maxWidth: '400px' }}>
                <Card className="shadow-sm p-4">
                    <Card.Body>
                        <h3 className="text-center mb-4">Vendor Sign In</h3>
                        {error && <Alert variant="danger">{error}</Alert>}

                        <Form onSubmit={handleSubmit}>
                            <Form.Group controlId="email" className="mb-3">
                                <Form.Label>Email</Form.Label>
                                <Form.Control
                                    type="email"
                                    placeholder="Enter your email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    required
                                />
                            </Form.Group>

                            <Form.Group controlId="password" className="mb-3">
                                <Form.Label>Password</Form.Label>
                                <Form.Control
                                    type="password"
                                    placeholder="Enter your password"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    required
                                />
                            </Form.Group>

                            <div className="d-flex justify-content-between align-items-center mt-4">
                                <Button variant="primary" type="submit" className="w-50">
                                    Sign In
                                </Button>
                                <Button
                                    variant="link"
                                    className="text-primary ms-3 p-0"
                                    onClick={() => setShowRegisterModal(true)}
                                >
                                    Register
                                </Button>
                            </div>
                        </Form>
                    </Card.Body>
                </Card>
            </div>
        </>
    );
};

export default Login;
