import React from 'react';
import SearchDropdown from './SearchDropdown'; // Import SearchDropdown component

const CategoryFilter = ({ selectedCategory, onCategoryChange, onSearch, onFilter }) => {
  const categories = ['All', 'Flower', 'Pre-roll', 'Vape', 'Edible', 'Extract', 'Misc'];

  return (
    <div
      className="category-filter sticky-top border"
      style={{
        backgroundColor: '#FFFFF0',
        padding: '0.5rem 1rem',
        display: 'flex',
        alignItems: 'center',
        gap: '1rem',
        position: 'sticky',
        top: '0',
        zIndex: '1000',
      }}
    >
      {/* Search Dropdown */}
      <SearchDropdown onSearch={onSearch} onFilter={onFilter} />

      {/* Category Links */}
      <div
        style={{
          display: 'flex',
          gap: '1rem',
          overflowX: 'auto',
          whiteSpace: 'nowrap',
          flexGrow: 1,
        }}
      >
        {categories.map((category) => (
          <span
            key={category}
            onClick={() => onCategoryChange(category)}
            style={{
              cursor: 'pointer',
              fontWeight: selectedCategory === category ? 'bold' : 'normal',
              color: selectedCategory === category ? '#242424' : '#4E4E4E',
              textTransform: 'uppercase',
              borderBottom: selectedCategory === category ? '2px solid #242424' : 'none',
              paddingBottom: '0.25rem',
              transition: 'color 0.3s ease, border-bottom 0.3s ease',
            }}
          >
            {category}
          </span>
        ))}
      </div>
    </div>
  );
};

export default CategoryFilter;
