import React, { useState } from 'react';
import { Form, Button, Card } from 'react-bootstrap';
import axios from 'axios';


const DailyPromoForm = ({ apiUrl, vendorId }) => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [applicableToSaleItems, setApplicableToSaleItems] = useState(false);
    const [promoFile, setPromoFile] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleFileChange = (e) => {
        setPromoFile(e.target.files[0]);
    };


    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        const formData = new FormData();
        formData.append('title', title);
        formData.append('description', description);
        formData.append('applicableToSaleItems', applicableToSaleItems);
        if (promoFile) {
            formData.append('promoImage', promoFile);
        }

        try {
            const response = await axios.post(`${apiUrl}/vendors/daily-promo`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    Authorization: `Bearer ${localStorage.getItem('token')}`,
                },
            });
            alert('Promo saved successfully!');
        } catch (err) {
            console.error('Error saving promo:', err);
            alert('Failed to save promo.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <Card className="shadow-sm p-4">
            <h4>Create Daily Promo</h4>
            <Form onSubmit={handleSubmit}>
                <Form.Group controlId="promoTitle" className="mb-3">
                    <Form.Label>Title</Form.Label>
                    <Form.Control
                        type="text"
                        placeholder="Enter promo title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        required
                    />
                </Form.Group>

                <Form.Group controlId="promoDescription" className="mb-3">
                    <Form.Label>Description</Form.Label>
                    <Form.Control
                        as="textarea"
                        rows={3}
                        placeholder="Enter promo description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        required
                    />
                </Form.Group>

                <Form.Group controlId="applicableToSaleItems" className="mb-3">
                    <Form.Check
                        type="checkbox"
                        label="Applicable to sale items"
                        checked={applicableToSaleItems}
                        onChange={(e) => setApplicableToSaleItems(e.target.checked)}
                    />
                </Form.Group>

                <Form.Group controlId="promoImage" className="mb-3">
                    <Form.Label>Promo Image</Form.Label>
                    <Form.Control type="file" accept="image/*" onChange={handleFileChange} />
                </Form.Group>

                <Button variant="primary" type="submit" disabled={loading}>
                    {loading ? 'Saving...' : 'Save Promo'}
                </Button>
            </Form>
        </Card>
    );
};

export default DailyPromoForm;
