import React, { useContext, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import StoreDetails from './StoreDetails';
import ProductList from './ProductList';
import { VendorProvider, VendorContext } from '../context/VendorContext';

const VendorStorePage = () => {
  const { vendorId } = useParams(); // Get vendorId from URL

  return (
    <VendorProvider vendorId={vendorId}>
      <VendorPageContent vendorId={vendorId} />
    </VendorProvider>
  );
};

const VendorPageContent = ({ vendorId }) => {
  const { vendor, products, error, fetchVendorData } = useContext(VendorContext);

  useEffect(() => {
    // Fetch vendor and product data when the component mounts
    if (vendorId) {
      fetchVendorData(vendorId);
    }
  }, [vendorId, fetchVendorData]);

  if (error) return <div>Error: {error}</div>;
  if (!vendor || !products) return <div>Loading...</div>;

  return (
    <div>
      <StoreDetails />
      <ProductList />
    </div>
  );
};

export default VendorStorePage;
