import React, { useEffect, useRef } from 'react';

const AddressInput = ({ onAddressSelect }) => {
  const inputRef = useRef(null);

  useEffect(() => {
    const initializeAutocomplete = async () => {
      try {
        const { Autocomplete } = await google.maps.importLibrary('places');

        if (inputRef.current) {
          const autocomplete = new Autocomplete(inputRef.current, {
            types: ['geocode'], // Restrict suggestions to addresses
            componentRestrictions: { country: 'us' }, // Restrict to US
          });

          autocomplete.addListener('place_changed', () => {
            const place = autocomplete.getPlace();
            if (place.geometry) {
              const selectedAddress = {
                lat: place.geometry.location.lat(),
                lng: place.geometry.location.lng(),
                address: place.formatted_address,
              };
              console.log('Selected Address:', selectedAddress);

              if (onAddressSelect) {
                onAddressSelect(selectedAddress); // Pass the selected address to the parent component
              }
            }
          });
        }
      } catch (error) {
        console.error('Error initializing Autocomplete:', error);
      }
    };

    const checkGoogleNamespace = setInterval(() => {
      if (window.google && window.google.maps && google.maps.importLibrary) {
        clearInterval(checkGoogleNamespace);
        initializeAutocomplete();
      }
    }, 100); // Check every 100ms

    return () => clearInterval(checkGoogleNamespace);
  }, []);

  return <input ref={inputRef} type="text" placeholder="Enter your address" style={{ width: '100%', padding: '8px' }} />;
};

export default AddressInput;
