import React, { useState, useEffect, useContext } from 'react';
import { Card, Button, Modal, Form, Alert } from 'react-bootstrap';
import axios from 'axios';
import { AuthContext } from '../context/AuthContext'; // Adjust the path to your AuthContext file

const EditAccountCard = () => {
    const { vendorId, token } = useContext(AuthContext); // Get vendorId and token from context
    const [vendor, setVendor] = useState({});
    const [showModal, setShowModal] = useState(false);
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        contactNumber: '',
        email: '',
    });
    const [alert, setAlert] = useState({ message: '', variant: '' });

    // Fetch vendor details on component mount using vendorId from AuthContext
    useEffect(() => {
        const fetchVendor = async () => {
            if (!vendorId || !token) return; // Ensure vendorId and token are available

            try {
                const response = await axios.get(`/api/vendors/${vendorId}`, {
                    headers: {
                        Authorization: `Bearer ${token}`, // Use token from context
                    },
                });
                setVendor(response.data.data);
            } catch (error) {
                console.error('Error fetching vendor details:', error);
            }
        };
        fetchVendor();
    }, [vendorId, token]);

    // Open modal and prefill form with current details
    const handleEdit = () => {
        setFormData({
            firstName: vendor.firstName || '',
            lastName: vendor.lastName || '',
            contactNumber: vendor.contactNumber || '',
            email: vendor.email || '',
        });
        setShowModal(true);
    };

    // Handle form input changes
    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    // Handle form submission using Bearer Token
    const handleUpdate = async () => {
        try {
            const response = await axios.put(`/api/vendors/${vendorId}/update-profile`, formData, {
                headers: {
                    Authorization: `Bearer ${token}`, // Use token from context
                },
            });
            setVendor(response.data.data); // Update vendor details in state
            setAlert({ message: 'Account details updated successfully!', variant: 'success' });
            setShowModal(false); // Close modal
        } catch (error) {
            console.error('Error updating account details:', error);
            setAlert({ message: 'Failed to update account details. Please try again.', variant: 'danger' });
        }
    };

    return (
        <div>
            {/* Display alert if available */}
            {alert.message && <Alert variant={alert.variant} className="mt-3">{alert.message}</Alert>}

            {/* Vendor Details Card */}
            <Card className="shadow-sm" style={{ width: '24rem', margin: '1rem auto' }}>
                <Card.Body>
                    <Card.Title>Account Details</Card.Title>
                    <Card.Text>
                        <strong>First Name:</strong> {vendor.firstName || 'N/A'} <br />
                        <strong>Last Name:</strong> {vendor.lastName || 'N/A'} <br />
                        <strong>Contact Number:</strong> {vendor.contactNumber || 'N/A'} <br />
                        <strong>Email:</strong> {vendor.email || 'N/A'}
                    </Card.Text>
                    <Button variant="link" onClick={handleEdit}>
                        Edit
                    </Button>
                </Card.Body>
            </Card>

            {/* Edit Modal */}
            <Modal show={showModal} onHide={() => setShowModal(false)} centered>
                <Modal.Header closeButton>
                    <Modal.Title>Edit Account Details</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="mb-3">
                            <Form.Label>First Name</Form.Label>
                            <Form.Control
                                type="text"
                                name="firstName"
                                value={formData.firstName}
                                onChange={handleChange}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Last Name</Form.Label>
                            <Form.Control
                                type="text"
                                name="lastName"
                                value={formData.lastName}
                                onChange={handleChange}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Contact Number</Form.Label>
                            <Form.Control
                                type="text"
                                name="contactNumber"
                                value={formData.contactNumber}
                                onChange={handleChange}
                            />
                        </Form.Group>
                        <Form.Group className="mb-3">
                            <Form.Label>Email</Form.Label>
                            <Form.Control
                                type="email"
                                name="email"
                                value={formData.email}
                                onChange={handleChange}
                            />
                        </Form.Group>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowModal(false)}>
                        Cancel
                    </Button>
                    <Button variant="primary" onClick={handleUpdate}>
                        Update
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default EditAccountCard;
