import React, { useState, useContext, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Row, Col, Form, Card, Container } from 'react-bootstrap';
import { CartContext } from '../context/CartContext';

const PlaceOrder = () => {
  const {
    calculateTotal,
    cartItems,
    clearCart,
    incrementItem,
    decrementItem,
    removeFromCart,
  } = useContext(CartContext);

  const [contactInfo, setContactInfo] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    birthdate: '',
  });

  const [savedAddress, setSavedAddress] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [newAddress, setNewAddress] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('');
  const [vendorPaymentMethods, setVendorPaymentMethods] = useState([]);
  const [vendorDetails, setVendorDetails] = useState(null);
  const inputRef = useRef(null);

  const navigate = useNavigate();
  const subtotal = calculateTotal();
  const estimatedTotal = subtotal;

  // Load vendor details and Google Maps
  useEffect(() => {
    const loadVendorAndGoogleMaps = async () => {
      // Load Google Maps
      if (!window.google) {
        const script = document.createElement('script');
        script.src = `https://maps.googleapis.com/maps/api/js?key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}&libraries=places`;
        script.async = true;
        script.defer = true;
        document.head.appendChild(script);
      }

      // Fetch vendor details
      if (cartItems[0]?.vendorId) {
        try {
          const response = await fetch(`http://localhost:5001/api/vendors/${cartItems[0].vendorId}`);
          const vendorData = await response.json();
          setVendorDetails(vendorData.data);
          setVendorPaymentMethods(vendorData.data.acceptedPayments || []);
        } catch (error) {
          console.error('Error fetching vendor data:', error);
        }
      }
    };

    loadVendorAndGoogleMaps();
  }, [cartItems]);

  // Load saved address and contact info
  useEffect(() => {
    const address = localStorage.getItem('userAddress');
    if (address) setSavedAddress(address);

    const storedContactInfo = JSON.parse(localStorage.getItem('contactInfo'));
    if (storedContactInfo) setContactInfo(storedContactInfo);
  }, []);

  // Setup Google Places Autocomplete
  useEffect(() => {
    if (isEditing && inputRef.current && window.google?.maps?.places) {
      const autocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
        types: ['geocode'],
      });

      autocomplete.addListener('place_changed', () => {
        const place = autocomplete.getPlace();
        if (place?.formatted_address) {
          setNewAddress(place.formatted_address);
        }
      });
    }
  }, [isEditing]);

  // Save contact info to localStorage
  useEffect(() => {
    localStorage.setItem('contactInfo', JSON.stringify(contactInfo));
  }, [contactInfo]);

  const formatBirthdate = (value) => {
    const digits = value.replace(/\D/g, '');
    const parts = [digits.slice(0, 2), digits.slice(2, 4), digits.slice(4, 8)].filter(Boolean);
    return parts.join('/');
  };

  const handleBirthdateChange = (e) => {
    const formattedValue = formatBirthdate(e.target.value);
    setContactInfo({ ...contactInfo, birthdate: formattedValue });
  };

  const handleSaveAddress = () => {
    localStorage.setItem('userAddress', newAddress);
    setSavedAddress(newAddress);
    setIsEditing(false);
  };

  const renderLocationSection = () => {
    const isPickup = vendorDetails?.dispensaryType === 'Pickup' || 
                    (vendorDetails?.dispensaryType === 'Pickup & Delivery' && 
                     cartItems[0]?.orderType === 'Pickup');

    if (isPickup) {
      return (
        <div className="mb-4">
          <h4 className="mb-3">Pickup Location</h4>
          <Card className="border-0 shadow-sm">
            <Card.Body className="d-flex align-items-center">
              <img 
                src={vendorDetails.logoUrl} 
                alt={vendorDetails.dispensaryName}
                className="me-3"
                style={{ width: '60px', height: '60px', objectFit: 'cover', borderRadius: '8px' }}
              />
              <div>
                <h5 className="mb-1">{vendorDetails.dispensaryName}</h5>
                <p className="text-secondary mb-0">
                  {vendorDetails.storefrontAddress?.formatted || 'Address not available'}
                </p>
              </div>
            </Card.Body>
          </Card>
        </div>
      );
    }

    return (
      <div className="mb-4">
        <h4 className="mb-3">Delivery Address</h4>
        {isEditing ? (
          <Form>
            <Form.Group controlId="address" className="mb-3">
              <Form.Control
                type="text"
                ref={inputRef}
                value={newAddress}
                onChange={(e) => setNewAddress(e.target.value)}
                placeholder="Enter your address"
                required
                className="border-0 shadow-sm"
              />
            </Form.Group>
            <Button variant="primary" onClick={handleSaveAddress} className="shadow-sm">
              Save Address
            </Button>
          </Form>
        ) : (
          <Card className="border-0 shadow-sm">
            <Card.Body>
              <p className="text-secondary mb-2">{savedAddress || 'No address saved'}</p>
              <Button
                variant="outline-primary"
                onClick={() => {
                  setNewAddress(savedAddress);
                  setIsEditing(true);
                }}
                className="shadow-sm"
              >
                {savedAddress ? 'Edit Address' : 'Add Address'}
              </Button>
            </Card.Body>
          </Card>
        )}
      </div>
    );
  };

  // Rest of the component remains the same...
  // Include all the existing handlePlaceOrder and other functions

  return (
    <div>
      <Container fluid className="place-order-section py-4">
        <Row className="g-4">
          <Col lg={6} xs={12}>
            <Card className="border-0 shadow">
              <Card.Body>
                {renderLocationSection()}

                <hr className="my-4" />

                {/* Contact Information Section */}
                <h4 className="mb-3">Contact Information</h4>
                {/* Rest of the existing contact form... */}

                {/* Payment Methods Section */}
                <h4 className="mb-3 mt-4">Payment Method</h4>
                <div className="d-flex flex-wrap gap-2">
                  {vendorPaymentMethods.map((method, index) => (
                    <Button
                      key={index}
                      variant={paymentMethod === method.method ? 'primary' : 'outline-primary'}
                      className="shadow-sm"
                      onClick={() => setPaymentMethod(method.method)}
                    >
                      {method.fee > 0
                        ? `${method.method} ($${method.fee.toFixed(2)} Fee)`
                        : method.method}
                    </Button>
                  ))}
                </div>
              </Card.Body>
            </Card>
          </Col>

          {/* Order Summary Column */}
          <Col lg={6} xs={12}>
            {/* Existing order summary code... */}
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default PlaceOrder;
