import React, { createContext, useState, useEffect } from 'react';

export const VendorContext = createContext();

export const VendorProvider = ({ vendorId, children }) => {
  const [vendor, setVendor] = useState(null);
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  // Function to fetch vendor data based on vendorId
  const fetchVendorData = async (id) => {
    try {
      const response = await fetch(`http://localhost:5001/api/vendors/${id}`);
      if (!response.ok) throw new Error(`Error fetching vendor data: ${response.statusText}`);
      const data = await response.json();
      setVendor(data.data); // Assuming vendor data is under data.data
    } catch (err) {
      console.error(err);
      setError("Failed to load vendor data.");
    }
  };

  // Function to fetch products for the specific vendor
  const fetchProductsData = async (id) => {
    try {
      const response = await fetch(`http://localhost:5001/api/products/vendor/${id}/products`);
      if (!response.ok) throw new Error(`Error fetching products data: ${response.statusText}`);
      const data = await response.json();
      setProducts(data.data.products); // Assuming products are under data.data.products
    } catch (err) {
      console.error(err);
      setError("Failed to load products data.");
    }
  };

  // Fetch data when vendorId changes
  useEffect(() => {
    if (vendorId) {
      fetchVendorData(vendorId);
      fetchProductsData(vendorId);
    }
  }, [vendorId]);

  return (
    <VendorContext.Provider value={{ vendor, products, error }}>
      {children}
    </VendorContext.Provider>
  );
};
