import React, { useState, useCallback, useEffect } from 'react';
import { Tabs, Tab, Col, Row, Accordion } from 'react-bootstrap';
import RecentOrders from '../components/RecentOrders';
import InventoryComponent from '../components/InventoryComponent';
import UpdateDeliveryZoneComponent from '../components/UpdateDeliveryZoneComponent';
import BusinessHoursComponent from '../components/BusinessHoursComponent';
import MinOrderComponent from '../components/MinOrderComponent';
import { loadGoogleMapsScript } from '../utils/googleMapsLoader';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import DailyPromoForm from './DailyPromoForm';
import DashNavBar from '../components/DashNavBar';
import api from '../utils/api';
import AddProductPage from '../components/AddProductPage';
import Footer from '../components/Footer';

const defaultHours = {
  monday: '',
  tuesday: '',
  wednesday: '',
  thursday: '',
  friday: '',
  saturday: '',
  sunday: '',
};

const generateTimeSlots = () => {
  const slots = [];
  let time = 6 * 60;
  const end = 24 * 60;

  while (time < end) {
    const hours = Math.floor(time / 60);
    const label = `${hours % 12 || 12}:00 ${hours < 12 ? 'AM' : 'PM'}`;
    slots.push(label);
    time += 60;
  }
  return slots;
};

const VendorDashboard = () => {
  const { logout } = useAuth();
  const navigate = useNavigate();
  const [vendorData, setVendorData] = useState(null);
  const [initialHours, setInitialHours] = useState(defaultHours);
  const [initialMinOrder, setInitialMinOrder] = useState(0);
  const [initialAcceptedPayments, setInitialAcceptedPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [mapsLoaded, setMapsLoaded] = useState(false);
  const [mapsError, setMapsError] = useState(null);

  // Load Google Maps script
  useEffect(() => {
    loadGoogleMapsScript()
      .then(() => setMapsLoaded(true))
      .catch((error) => {
        console.error('Error loading Google Maps:', error);
        setMapsError(error.message);
      });
  }, []);

  // Handle expired token
  const handleExpiredToken = useCallback(() => {
    console.warn('Session expired. Logging out.');
    logout();
    navigate('/login');
  }, [logout, navigate]);

  // Fetch vendor data
  const fetchVendorData = useCallback(async () => {
    try {
      const response = await api.get('/vendors/profile');
      const { data } = response.data;

      // Format business hours
      const formattedHours = {};
      if (data.businessHours) {
        Object.keys(data.businessHours).forEach(day => {
          if (data.businessHours[day]) {
            formattedHours[day] = {
              open: data.businessHours[day].open || '',
              close: data.businessHours[day].close || ''
            };
          }
        });
      }

      setInitialHours(formattedHours);
      setInitialMinOrder(data.minOrder || 0);
      setInitialAcceptedPayments(data.acceptedPayments || []);
      setVendorData(data);
      setError(null);
    } catch (error) {
      console.error('Error fetching vendor data:', error.message);

      if (error.response?.status === 401) {
        handleExpiredToken();
      } else {
        setError('Failed to fetch vendor data.');
      }
    } finally {
      setLoading(false);
    }
  }, [handleExpiredToken]);

  // Initial data fetch
  useEffect(() => {
    fetchVendorData();
  }, [fetchVendorData]);

  // Handle settings update
  const handleUpdateSettings = async (formData) => {
    try {
      const response = await api.put('/vendors/min-order', {
        minOrder: formData.minOrder,
        acceptedPayments: formData.acceptedPayments
      });

      if (response.data.success) {
        setInitialMinOrder(formData.minOrder);
        setInitialAcceptedPayments(formData.acceptedPayments);
        alert('Settings updated successfully!');
      } else {
        alert(response.data.message || 'Failed to update settings.');
      }
    } catch (error) {
      console.error('Error updating settings:', error);
      alert('An error occurred while updating the settings. Please try again.');
    }
  };

  // Loading and error states
  if (loading) return <div>Loading dashboard data...</div>;
  if (error) return <div className="alert alert-danger">{error}</div>;
  if (mapsError) return <div>Error loading maps: {mapsError}</div>;
  if (!mapsLoaded) return <div>Loading map...</div>;

  return (
    <div style={{ paddingTop: '60px' }}>
      <DashNavBar vendorData={vendorData} />
      <Tabs 
        defaultActiveKey="orders" 
        id="vendor-dashboard-tabs" 
        className="mb-3"
        style={{
          backgroundColor: '#F1F1F1',
          padding: '10px 10px 0 10px'
        }}
      >
        <style type="text/css">
          {`
            .nav-tabs {
              border-bottom: none;
            }
            .nav-tabs .nav-link {
              background-color: #FFF;
              border: none;
              margin-right: 5px;
              color: #333;
              border-radius: 4px 4px 0 0;
            }
            .nav-tabs .nav-link:hover {
              border: none;
              background-color: #F8F9FA;
            }
            .nav-tabs .nav-link.active {
              background-color: #FFF;
              border: none;
              color: #333;
            }
            .tab-content {
              background-color: #FFF;
              padding: 20px;
              border-radius: 0 0 4px 4px;
            }
          `}
        </style>

        <Tab eventKey="orders" title="Orders">
          <RecentOrders />
        </Tab>

        <Tab eventKey="inventory" title="Menu">
          <InventoryComponent />
        </Tab>

        <Tab eventKey="products" title="Add Products">
          <AddProductPage />
        </Tab>

        <Tab eventKey="profile" title="Settings">
          <div className="p-0">
            <Accordion defaultActiveKey="0">
              <Accordion.Item eventKey="0">
                <Accordion.Header>Delivery Zone</Accordion.Header>
                <Accordion.Body>
                  <Row className="g-4">
                    <UpdateDeliveryZoneComponent 
                      isLoaded={mapsLoaded} 
                      loadError={mapsError}
                    />
                  </Row>
                </Accordion.Body>
              </Accordion.Item>

              <Accordion.Item eventKey="1">
                <Accordion.Header>Business Hours</Accordion.Header>
                <Accordion.Body>
                  <BusinessHoursComponent
                    initialHours={initialHours}
                    onUpdate={async (updatedHours) => {
                      try {
                        const response = await api.put('/vendors/business-hours', {
                          businessHours: updatedHours
                        });

                        if (response.data.success) {
                          setInitialHours(updatedHours);
                          alert('Business hours updated successfully!');
                        } else {
                          alert(response.data.message || 'Failed to update business hours.');
                        }
                      } catch (error) {
                        console.error('Error updating business hours:', error);
                        alert('An error occurred while updating business hours. Please try again.');
                      }
                    }}
                    availableTimes={generateTimeSlots()}
                  />
                </Accordion.Body>
              </Accordion.Item>

              <Accordion.Item eventKey="2">
                <Accordion.Header>Daily Promotion</Accordion.Header>
                <Accordion.Body>
                  <DailyPromoForm />
                </Accordion.Body>
              </Accordion.Item>

              <Accordion.Item eventKey="3">
                <Accordion.Header>Payment Settings</Accordion.Header>
                <Accordion.Body>
                  <Col xs={12}>
                    <MinOrderComponent
                      minOrder={initialMinOrder}
                      acceptedPayments={initialAcceptedPayments}
                      onUpdate={handleUpdateSettings}
                    />
                  </Col>
                </Accordion.Body>
              </Accordion.Item>
            </Accordion>
          </div>
        </Tab>
      </Tabs>
    </div>
  );
};

export default VendorDashboard;
