import React, { useState, useEffect } from 'react';
import { Tabs, Tab } from 'react-bootstrap';
import RecentOrders from './RecentOrders';
import InventoryComponent from './InventoryComponent';
import LogoutButton from './LogoutButton';
import UpdateDeliveryZoneComponent from './UpdateDeliveryZoneComponent';
import BusinessHoursComponent from './BusinessHoursComponent';
import { useLoadScript } from '@react-google-maps/api';

const libraries = ['places'];
const generateTimeSlots = () => {
  const slots = [];
  let time = 6 * 60; // Start at 6:00 AM in minutes
  const end = 24 * 60; // End at midnight (24:00)

  while (time < end) {
    const hours = Math.floor(time / 60);
    const minutes = time % 60;
    const label = `${hours % 12 || 12}:${minutes.toString().padStart(2, '0')} ${hours < 12 ? 'AM' : 'PM'}`;
    slots.push(label);
    time += 30; // Increment by 30 minutes
  }
  return slots;
};


const VendorDashboard = () => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: 'process.env.REACT_APP_GOOGLE_MAPS_API_KEY',
    libraries,
  });

  const [initialHours, setInitialHours] = useState(null);

  const fetchBusinessHours = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/vendors/profile', {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!response.ok) {
        throw new Error('Failed to fetch business hours');
      }
      const data = await response.json();
      console.log('Fetched business hours:', data.data.businessHours); // Debug log
      setInitialHours(data.data.businessHours || {}); // Default to an empty object if no hours exist
    } catch (error) {
      console.error('Error fetching business hours:', error);
    }
  };

  useEffect(() => {
    fetchBusinessHours(); // Call the function when the component mounts
  }, []);

  // Handle updating business hours
  const handleUpdate = async (updatedHours) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/vendors/business-hours', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ businessHours: updatedHours }),
      });

      if (response.ok) {
        alert('Business hours updated successfully!');
        setInitialHours(updatedHours); // Update local state
      } else {
        throw new Error('Failed to update business hours');
      }
    } catch (error) {
      console.error('Error updating business hours:', error);
      alert('An error occurred while updating business hours.');
    }
  };

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading map...</div>;

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center m-4">
        <h2 className="flex-grow-1 text-center">Vendor Dashboard</h2>
        <LogoutButton />
      </div>
      <Tabs defaultActiveKey="orders" id="vendor-dashboard-tabs" className="mb-3">
        <Tab eventKey="orders" title="Orders">
          <RecentOrders />
        </Tab>
        <Tab eventKey="inventory" title="Inventory">
          <InventoryComponent />
        </Tab>
        <Tab eventKey="profile" title="Profile">
          <div>
            <h2>Vendor Profile</h2>
            <UpdateDeliveryZoneComponent isLoaded={isLoaded} loadError={loadError} />
            {initialHours ? (
              <BusinessHoursComponent
                initialHours={initialHours}
                onUpdate={handleUpdate}
                availableTimes={generateTimeSlots()}
              />
            ) : (
              <div>Loading business hours...</div>
            )}
          </div>
        </Tab>
      </Tabs>
    </div>
  );
};

export default VendorDashboard;
