/* global google */
import React, { useState } from 'react';
import { GoogleMap, Marker } from '@react-google-maps/api';

const containerStyle = {
  width: '100%',
  height: '400px',
};

function MapComponent({ onLocationChange }) {
  const [location, setLocation] = useState(null); // No initial location

  const handleMapClick = (event) => {
    const newLocation = {
      lat: event.latLng.lat(),
      lng: event.latLng.lng(),
    };
    setLocation(newLocation); // Place the marker where the user clicked
    onLocationChange(newLocation); // Notify parent of the new location
  };

  const handleMarkerDragEnd = (event) => {
    const newLocation = {
      lat: event.latLng.lat(),
      lng: event.latLng.lng(),
    };
    setLocation(newLocation); // Update marker location
    onLocationChange(newLocation); // Notify parent of the updated location
  };

  return (
    <GoogleMap
      mapContainerStyle={containerStyle}
      center={{ lat: 34.0522, lng: -118.2437 }} // Default center
      zoom={10}
      onClick={handleMapClick} // Handle user clicks on the map
    >
      {location && (
        <Marker
          position={location}
          draggable
          onDragEnd={handleMarkerDragEnd} // Allow user to drag the marker
        />
      )}
    </GoogleMap>
  );
}

export default MapComponent;
