import React from 'react';
import { Alert } from 'react-bootstrap';

const StoreNoticeComponent = ({ storeNotice }) => {
  return (
    <div className="my-4">
      <h5 className="text-center">Store Notice</h5>
      {storeNotice ? (
        <div className="d-flex justify-content-center">
          <div className="p-3 rounded bg-light text-center shadow-sm" style={{ maxWidth: '600px', border: '1px solid #ddd' }}>
            <p className="mb-0 text-muted">
              <i className="bi bi-chat-left-quote text-primary"></i> {storeNotice}
            </p>
          </div>
        </div>
      ) : (
        <Alert variant="warning" className="text-center">
          No store notice has been set yet.
        </Alert>
      )}
    </div>
  );
};

export default StoreNoticeComponent;
