import React, { useEffect, useRef, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const AddressPage = () => {
  const [address, setAddress] = useState('');
  const navigate = useNavigate();
  const inputRef = useRef(null);

  // Redirect if address exists in localStorage
  useEffect(() => {
    const savedAddress = localStorage.getItem('userAddress');
    if (savedAddress) {
      navigate('/nearby');
    }
  }, [navigate]);

  useEffect(() => {
    if (!window.google) {
      console.error('Google Maps API is not loaded.');
      return;
    }

    const autocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
      types: ['geocode'], // Restrict to address suggestions
    });

    autocomplete.addListener('place_changed', () => {
      const place = autocomplete.getPlace();
      if (place && place.formatted_address) {
        setAddress(place.formatted_address);

        // Save the address and navigate to the next page
        localStorage.setItem('userAddress', place.formatted_address);
        navigate('/nearby');
      }
    });
  }, [navigate]);

  return (
    <div className="d-flex justify-content-center align-items-center vh-100" style={{ backgroundColor: '#0B0B0B' }}>
      <div
        className="card shadow"
        style={{
          width: '30rem',
          padding: '1.5rem',
          backgroundColor: '#F2ED6B',
          borderRadius: '1rem',
        }}
      >
        <div className="card-body text-center">
          {/* Replace heading with logo */}
          <img
            src={require('../assets/images/logo.png')}
            alt="WeedHurry Logo"
            className="mb-4"
            style={{ maxWidth: '100%', height: 'auto' }}
          />
          <p className="card-text mb-4" style={{ color: '#333333', fontWeight: '500' }}>
            Find top brand cannabis products and delivery/pickup nearest you!
          </p>
          <div className="input-group">
            <input
              type="text"
              ref={inputRef}
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              placeholder="Enter your address"
              className="form-control rounded-pill px-3"
              style={{
                border: '1px solid #ced4da',
                paddingRight: '2.5rem',
                backgroundColor: '#ffffff',
              }}
            />
            <span
              className="input-group-text bg-transparent border-0"
              style={{
                position: 'absolute',
                right: '10px',
                top: '50%',
                transform: 'translateY(-50%)',
                cursor: 'pointer',
              }}
            >
              &gt;
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddressPage;
