import React, { useState } from "react";
import { Form, Button, Alert, Row, Col, InputGroup, Card } from "react-bootstrap";
import axios from "axios";
import { Link } from "react-router-dom";
import "./VendorRegistration.css"; // Import CSS for animation

const VendorRegistration = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    dispensaryName: "",
    contactNumber: "",
    email: "",
    password: "",
    minOrder: "",
    acceptedPayments: [{ method: "Cash", fee: 0 }],
  });
  const [error, setError] = useState(null); // Backend error handling
  const [success, setSuccess] = useState(false); // Registration success message
  const [formErrors, setFormErrors] = useState({}); // Validation error messages

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handlePhoneChange = (e) => {
    const rawValue = e.target.value.replace(/\D/g, ""); // Remove non-numeric characters
    const formatted = rawValue
      .replace(/^(\d{0,3})(\d{0,3})(\d{0,4})$/, (match, areaCode, prefix, lineNumber) => {
        if (lineNumber) return `(${areaCode}) ${prefix}-${lineNumber}`;
        if (prefix) return `(${areaCode}) ${prefix}`;
        if (areaCode) return `(${areaCode}`;
        return "";
      });
    setFormData((prev) => ({
      ...prev,
      contactNumber: formatted,
    }));
  };

  const handlePaymentFeeChange = (index, value) => {
    setFormData((prev) => {
      const updatedPayments = [...prev.acceptedPayments];
      updatedPayments[index].fee = parseFloat(value) || 0; // Ensure fee is numeric
      return { ...prev, acceptedPayments: updatedPayments };
    });
  };

  const validateForm = () => {
    const errors = {};
    const phoneNumber = formData.contactNumber.replace(/\D/g, ""); // Extract digits from formatted phone number

    // Validate phone number
    if (phoneNumber.length !== 10) {
      errors.contactNumber = "Phone number must be exactly 10 digits.";
    }

    // Validate email
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) {
      errors.email = "Please enter a valid email address.";
    }

    // Add additional validation rules here if needed

    setFormErrors(errors);
    return Object.keys(errors).length === 0; // Return true if no errors
  };

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent default form submission
    setError(null); // Reset error state

    if (!validateForm()) {
      return; // Stop submission if validation fails
    }

    try {
      const formattedFormData = {
        ...formData,
        minOrder: parseFloat(formData.minOrder) || 0, // Ensure minOrder is numeric
      };
      const response = await axios.post("/api/auth/register", formattedFormData);

      if (response.data.success) {
        setSuccess(true);
      }
    } catch (err) {
      setError(err.response?.data?.message || "Error registering vendor. Please try again.");
    }
  };

  return (
    <div className="card-background d-flex justify-content-center align-items-end">
      <Card className="registration-card slide-up my-4 mx-3">
        <Card.Body>
          <h2 className="mb-4 text-center">Vendor Registration</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          {success &&<Alert variant="success">
              Vendor registered successfully!{" "}
              <Link to="/login" className="alert-link">Login</Link>
            </Alert>}

          <Form onSubmit={handleSubmit}>
            <Row>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>First Name</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Enter your first name"
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleChange}
                    required
                  />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group className="mb-3">
                  <Form.Label>Last Name</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Enter your last name"
                    name="lastName"
                    value={formData.lastName}
                    onChange={handleChange}
                    required
                  />
                </Form.Group>
              </Col>
            </Row>

            <Form.Group className="mb-3">
              <Form.Label>Dispensary Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter your dispensary name"
                name="dispensaryName"
                value={formData.dispensaryName}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Phone Number</Form.Label>
              <Form.Control
                type="text"
                name="contactNumber"
                placeholder="Enter your phone number"
                value={formData.contactNumber}
                onChange={handlePhoneChange}
                required
              />
              {formErrors.contactNumber && (
                <Form.Text className="text-danger">{formErrors.contactNumber}</Form.Text>
              )}
              <Form.Text className="text-muted">Used for SMS order notifications.</Form.Text>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter your email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
              {formErrors.email && (
                <Form.Text className="text-danger">{formErrors.email}</Form.Text>
              )}
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Create a password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Minimum Order</Form.Label>
              <InputGroup>
                <InputGroup.Text>$</InputGroup.Text>
                <Form.Control
                  type="number"
                  placeholder="Enter minimum order amount"
                  name="minOrder"
                  value={formData.minOrder}
                  onChange={handleChange}

                />
              </InputGroup>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Accepted Payments</Form.Label>
              <div>
                {["Cash", "Debit", "Credit"].map((method, index) => (
                  <div key={method} className="d-flex align-items-center mb-2">
                    <Form.Check
                      type="checkbox"
                      id={method}
                      label={method}
                      checked={formData.acceptedPayments.some(
                        (payment) => payment && payment.method === method
                      )}
                      onChange={(e) =>
                        setFormData((prev) => {
                          const updatedPayments = prev.acceptedPayments.filter(
                            (payment) => payment && payment.method !== method
                          );

                          if (e.target.checked) {
                            updatedPayments.push({ method, fee: 0 });
                          }

                          return { ...prev, acceptedPayments: updatedPayments };
                        })
                      }
                    />
                    {(method === "Debit" || method === "Credit") &&
                      formData.acceptedPayments.some(
                        (payment) => payment && payment.method === method
                      ) && (
                        <InputGroup className="ms-3" style={{ maxWidth: "150px" }}>
                          <InputGroup.Text>Fee:</InputGroup.Text>
                          <InputGroup.Text>$</InputGroup.Text>
                          <Form.Control
                            type="number"
                            maxLength="2"
                            placeholder="0"
                            value={
                              formData.acceptedPayments.find(
                                (payment) => payment && payment.method === method
                              )?.fee || ""
                            }
                            onChange={(e) =>
                              handlePaymentFeeChange(index, e.target.value.slice(0, 2))
                            }
                          />
                        </InputGroup>
                      )}
                  </div>
                ))}
              </div>
            </Form.Group>

            <Button variant="primary" type="submit" className="w-100">
              Register
            </Button>
          </Form>
        </Card.Body>
      </Card>
      <div 
  style="
    background-color: #f9f9f6; 
    border: 1px solid #ddd; 
    border-radius: 8px; 
    padding: 2rem; 
    margin-top: 2rem; 
    text-align: center; 
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);"
>
  <h3 style="color: #333; font-weight: bold;">Why Join WeedHurry?</h3>
  <p style="color: #555; margin: 1rem 0;">
    Signing up as a vendor is simple, fast, and completely <strong style="color: #28a745;">FREE</strong>.
  </p>
  <div 
    style="
      display: flex; 
      flex-wrap: wrap; 
      justify-content: center; 
      gap: 2rem; 
      margin-top: 1.5rem;"
  >
    <div style="max-width: 300px;">
      <img 
        src="/images/easy-to-join.png" 
        alt="Easy to Join" 
        style="width: 100px; height: auto; margin-bottom: 1rem;"
      />
      <h5 style="color: #333; font-weight: bold;">Effortless Onboarding</h5>
      <p style="color: #555; font-size: 0.9rem;">
        Get started in minutes. No lengthy processes or hidden charges.
      </p>
    </div>
    <div style="max-width: 300px;">
      <img 
        src="/images/delivery-zone.png" 
        alt="Delivery Zone" 
        style="width: 100px; height: auto; margin-bottom: 1rem;"
      />
      <h5 style="color: #333; font-weight: bold;">Custom Delivery Zones</h5>
      <p style="color: #555; font-size: 0.9rem;">
        Define your own delivery area to reach customers nearby.
      </p>
    </div>
    <div style="max-width: 300px;">
      <img 
        src="/images/track-visits.png" 
        alt="Track Visits" 
        style="width: 100px; height: auto; margin-bottom: 1rem;"
      />
      <h5 style="color: #333; font-weight: bold;">Track Your Success</h5>
      <p style="color: #555; font-size: 0.9rem;">
        Monitor visits to your page and grow your business with real insights.
      </p>
    </div>
  </div>
  <div style="margin-top: 2rem;">
    <button 
      style="
        background-color: #28a745; 
        color: white; 
        border: none; 
        padding: 0.75rem 1.5rem; 
        font-size: 1rem; 
        border-radius: 8px; 
        cursor: pointer;"
      onclick="document.getElementById('registrationForm').scrollIntoView({ behavior: 'smooth' })"
    >
      Start Now for Free
    </button>
  </div>
</div>

    </div>
  );
};

export default VendorRegistration;
