import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import { CartProvider } from './context/CartContext';
import ProtectedRoute from './components/ProtectedRoute';
import CustomNavbar from './components/CustomNavbar';
import Login from './components/Login';
import TestDashboard from './components/TestDashboard';
import VendorPage from './components/VendorPage';
import VendorStorePage from './components/VendorStorePage';
import CheckoutPage from './components/CheckoutPage';
import VendorRegistration from './components/VendorRegistration';
import TestMenu from './components/TestMenu';
import OrderConfirmationPage from './components/OrderConfirmationPage';
import AddressInput from './components/AddressInput';
import AddressPage from './components/AddressPage';
import NearbyVendorsPage from './components/NearbyVendorsPage';
import Cart from './components/Cart';
import VendorMenuPage from './components/VendorMenuPage';
import NearbyPickupPage from './components/NearbyPickupPage';
import DeliveryPickupPage from './components/DeliveryPickupPage';
import DailyPromoForm from './components/DailyPromoForm';
import RegistrationPage from './components/RegistrationPage';

function App() {
    const location = useLocation(); // Get the current location

    // Define routes where CustomNavbar should not appear
    const hideNavbarRoutes = ["/register"];

    const shouldShowNavbar = !hideNavbarRoutes.includes(location.pathname);
    return (
        <AuthProvider> {/* Provide authentication context */}
            <CartProvider> {/* Provide cart context */}
                <Router>
                {shouldShowNavbar && <CustomNavbar />}
                    <Cart />
                    <Routes>
                        {/* Public Routes */}
                        <Route path="/" element={<AddressPage />} />
                        <Route path="/nearby" element={<DeliveryPickupPage />} />
                        <Route path="/checkout" element={<CheckoutPage />} />
                        <Route path="/register" element={<RegistrationPage />} />
                        <Route path="/login" element={<Login />} />
                        <Route path="/menu" element={<TestMenu />} />
                        <Route path="/test" element={<DailyPromoForm />} />
                        <Route path="/order-confirmation/:orderId" element={<OrderConfirmationPage />} />
                        <Route path="/vendor/:vendorId" element={<VendorStorePage />} />
                        <Route path="/vendor/:vendorId/menu" element={<VendorMenuPage />} />

                        {/* Protected Routes */}
                        <Route path="/vendor-dashboard" element={<TestDashboard />} />
                    </Routes>
                </Router>
            </CartProvider>
        </AuthProvider>
    );
}

export default App;
