import React, { useState, useEffect, useRef } from "react";
import { Form, Button, Alert, Row, Col, Card } from "react-bootstrap";
import axios from "axios";
import { useJsApiLoader, GoogleMap, Marker } from "@react-google-maps/api";
import { Link } from "react-router-dom";
import "./VendorRegistration.css";

const GOOGLE_MAPS_LIBRARIES = ["places"];

const VendorRegistration = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    contactNumber: { formatted: "", e164: "" },
    email: "",
    password: "",
    dispensaryName: "",
    dispensaryType: "",
    storefrontAddress: {
      formatted: "",
      lng: null,
      lat: null
    },
    minOrder: "",
    acceptedPayments: [{ method: "Cash", fee: 0 }],
    deliveryZone: null,
  });

  const [formErrors, setFormErrors] = useState({});
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(null);
  const [animateCard, setAnimateCard] = useState(false);

  // Ref for Google Places Autocomplete
  const addressInputRef = useRef(null);

  const { isLoaded, loadError } = useJsApiLoader({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries: ['places'],
    onLoad: () => console.log('Google Maps API Loaded'),
    onError: (error) => console.error('Google Maps API Load Error:', error)
  });

  // Animation effect
  useEffect(() => {
    const timer = setTimeout(() => setAnimateCard(true), 100);
    return () => clearTimeout(timer);
  }, []);

  // Google Places Autocomplete Setup
  useEffect(() => {
    if (isLoaded && addressInputRef.current && window.google?.maps?.places) {
      const autocomplete = new window.google.maps.places.Autocomplete(
        addressInputRef.current,
        {
          types: ['address'],
          componentRestrictions: { country: 'us' }
        }
      );

      const handlePlaceSelect = () => {
        const place = autocomplete.getPlace();

        if (!place.geometry) {
          console.error('No geometry found for selected place');
          return;
        }

        const location = place.geometry.location;

        setFormData(prev => ({
          ...prev,
          storefrontAddress: {
            formatted: place.formatted_address || '',
            lng: location.lng(),
            lat: location.lat()
          }
        }));
      };

      autocomplete.addListener('place_changed', handlePlaceSelect);

      // Cleanup
      return () => {
        window.google.maps.event.clearInstanceListeners(autocomplete);
      };
    }
  }, [isLoaded]);

  // Form Handlers
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handlePhoneChange = (e) => {
    const rawValue = e.target.value.replace(/\D/g, "");

    const formatted = rawValue.replace(
      /^(\d{0,3})(\d{0,3})(\d{0,4})$/,
      (match, areaCode, prefix, lineNumber) => {
        if (lineNumber) return `(${areaCode}) ${prefix}-${lineNumber}`;
        if (prefix) return `(${areaCode}) ${prefix}`;
        if (areaCode) return `(${areaCode}`;
        return "";
      }
    );

    const e164 = rawValue.length === 10 ? `+1${rawValue}` : '';

    setFormData(prev => ({ 
      ...prev, 
      contactNumber: { formatted, e164 }
    }));
  };

  // Validation
  const validateStepOne = () => {
    const errors = {};
    if (!formData.firstName.trim()) errors.firstName = "First name is required";
    if (!formData.lastName.trim()) errors.lastName = "Last name is required";
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email))
      errors.email = "Invalid email address";
    if (!formData.contactNumber.e164 || formData.contactNumber.e164.replace(/^\+1/, '').length !== 10) {
      errors.contactNumber = "Phone number must be 10 digits";
    }
    if (formData.password.length < 6) errors.password = "Password must be at least 6 characters";

    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  // Form Submission Handlers
  const handleNext = (e) => {
    e.preventDefault();
    if (validateStepOne()) {
      setCurrentStep(2);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formattedFormData = {
        ...formData,
        minOrder: parseFloat(formData.minOrder) || 0,
      };

      // Validate storefront address for Pickup and Pickup & Delivery
      if (["Pickup", "Pickup & Delivery"].includes(formData.dispensaryType)) {
        if (!formData.storefrontAddress.lng || 
            !formData.storefrontAddress.lat || 
            !formData.storefrontAddress.formatted) {
          setError("Storefront address is incomplete or invalid.");
          return;
        }

        formattedFormData.storefrontAddress = {
          type: "Point",
          formatted: formData.storefrontAddress.formatted,
          coordinates: [
            formData.storefrontAddress.lng, 
            formData.storefrontAddress.lat
          ]
        };
      }

      const response = await axios.post("/api/auth/register", formattedFormData);
      if (response.data.success) setSuccess(true);
    } catch (err) {
      setError(err.response?.data?.message || "Error registering vendor.");
    }
  };

  // Render Conditions
  if (loadError) return <Alert variant="danger">Error loading Google Maps API</Alert>;
  if (!isLoaded) return <p>Loading...</p>;

  return (
    <div className="container py-5 mb-4">
      <div className="d-flex justify-content-center">
        <Card
          className={`w-100 shadow-sm animated-card ${animateCard ? "slide-in" : ""}`}
          style={{ maxWidth: "90%" }}
        >
          <Card.Body>
            {success ? (
              <div className="text-center">
                <Alert variant="success" className="mb-4">
                  Registration successful! Your account has been created.
                </Alert>
                <Link to="/login" className="btn btn-primary">
                  Go to Login
                </Link>
              </div>
            ) : (
              <>
                {error && <Alert variant="danger">{error}</Alert>}

                {currentStep === 1 && (
                  // First Step Form (Personal Details)
                  <Form onSubmit={handleNext}>
                    {/* Form fields for personal details */}
                    {/* (Previous implementation remains the same) */}
                  </Form>
                )}

                {currentStep === 2 && (
                  <Form onSubmit={handleSubmit}>
                    {/* Dispensary Details Form */}
                    {["Pickup", "Pickup & Delivery"].includes(formData.dispensaryType) && (
                      <Form.Group className="mb-3">
                        <Form.Label>Storefront Address</Form.Label>
                        <Form.Control
                          type="text"
                          ref={addressInputRef}
                          placeholder="Start typing to search for your address"
                          value={formData.storefrontAddress.formatted}
                          onChange={(e) => setFormData(prev => ({
                            ...prev,
                            storefrontAddress: {
                              ...prev.storefrontAddress,
                              formatted: e.target.value
                            }
                          }))}
                        />
                        {formData.storefrontAddress.formatted && (
                          <div className="mt-2 text-muted">
                            Selected: {formData.storefrontAddress.formatted}
                          </div>
                        )}
                      </Form.Group>
                    )}

                    {/* Delivery Zone Map (Previous implementation) */}
                  </Form>
                )}
              </>
            )}
          </Card.Body>
        </Card>
      </div>
    </div>
  );
};

export default VendorRegistration;
