import React, { useState, useRef, useEffect } from 'react';
import { Card, Form, Button } from 'react-bootstrap';

const AddressInput = ({ onAddressUpdate }) => {
  const [selectedAddress, setSelectedAddress] = useState(null);
  const inputRef = useRef(null);

  useEffect(() => {
    if (window.google && inputRef.current) {
      const autocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
        types: ['geocode'], // Limit suggestions to addresses
        componentRestrictions: { country: 'us' }, // Restrict to a specific country
      });

      autocomplete.addListener('place_changed', () => {
        const place = autocomplete.getPlace();
        if (place.geometry) {
          const location = {
            lat: place.geometry.location.lat(),
            lng: place.geometry.location.lng(),
            address: place.formatted_address,
          };

          setSelectedAddress(location.address);
          onAddressUpdate(location); // Pass address details to parent
        }
      });
    }
  }, []);

  return (
    <Card className="my-3 mx-auto" style={{ maxWidth: '500px' }}>
      <Card.Body>
        <Card.Title className="mb-3">Enter Your Address</Card.Title>
        <Form.Group controlId="addressInput" className="mb-3">
          <Form.Control
            ref={inputRef}
            type="text"
            placeholder="Start typing your address..."
            aria-label="Enter your address"
          />
        </Form.Group>
        {selectedAddress && (
          <div className="text-muted">
            <i className="bi bi-geo-alt-fill me-2"></i> Delivering to: <strong>{selectedAddress}</strong>
          </div>
        )}
        <Button variant="primary" className="mt-3">
          Confirm Address
        </Button>
      </Card.Body>
    </Card>
  );
};

export default AddressInput;
