import React, { useState } from "react";
import { Form, Button, Alert, Row, Col, InputGroup } from "react-bootstrap";
import axios from "axios";

const VendorRegistration = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    dispensaryName: "",
    contactNumber: "",
    email: "",
    password: "",
    minOrder: "",
    acceptedPayments: [
      { method: "Cash", fee: 0 },
      { method: "Debit", fee: 0 },
      { method: "Credit", fee: 0 },
    ],
  });
  const [error, setError] = useState(null); // Backend error handling
  const [success, setSuccess] = useState(false); // Registration success message

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handlePhoneChange = (e) => {
    const rawValue = e.target.value.replace(/\D/g, ""); // Remove non-numeric characters
    const formatted = rawValue
      .replace(/^(\d{3})(\d{3})(\d{4}).*/, "($1) $2-$3") // Format to (###) ###-####
      .slice(0, 14); // Limit to 14 characters
    setFormData((prev) => ({
      ...prev,
      contactNumber: formatted,
    }));
  };

  const handlePaymentFeeChange = (index, value) => {
    setFormData((prev) => {
      const updatedPayments = [...prev.acceptedPayments];
      updatedPayments[index].fee = parseFloat(value) || 0; // Ensure fee is numeric
      return { ...prev, acceptedPayments: updatedPayments };
    });
  };

  const handleSubmit = async () => {
    try {
      setError(null); // Reset error state
      const formattedFormData = {
        ...formData,
        minOrder: parseFloat(formData.minOrder) || 0, // Ensure minOrder is numeric
      };
      const response = await axios.post("/api/auth/register", formattedFormData);

      if (response.data.success) {
        setSuccess(true);
      }
    } catch (err) {
      setError(err.response?.data?.message || "Error registering vendor. Please try again.");
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4 text-center">Vendor Registration</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">Vendor registered successfully!</Alert>}

      <Form>
        <Row>
          <Col md={6}>
            <Form.Group className="mb-3">
              <Form.Label>First Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter your first name"
                name="firstName"
                value={formData.firstName}
                onChange={handleChange}
                required
              />
            </Form.Group>
          </Col>
          <Col md={6}>
            <Form.Group className="mb-3">
              <Form.Label>Last Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter your last name"
                name="lastName"
                value={formData.lastName}
                onChange={handleChange}
                required
              />
            </Form.Group>
          </Col>
        </Row>

        <Form.Group className="mb-3">
          <Form.Label>Dispensary Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter your dispensary name"
            name="dispensaryName"
            value={formData.dispensaryName}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Phone Number</Form.Label>
          <Form.Control
            type="text"
            placeholder="(###) ###-####"
            name="contactNumber"
            value={formData.contactNumber}
            onChange={handlePhoneChange}
            required
          />
          <Form.Text className="text-muted">Used for SMS order notifications.</Form.Text>
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            placeholder="Enter your email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Create a password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Minimum Order</Form.Label>
          <InputGroup>
            <InputGroup.Text>$</InputGroup.Text>
            <Form.Control
              type="number"
              placeholder="Enter minimum order amount"
              name="minOrder"
              value={formData.minOrder}
              onChange={handleChange}
              required
            />
          </InputGroup>
        </Form.Group>

        <Form.Group className="mb-3">
  <Form.Label>Accepted Payments</Form.Label>
  <div>
    {["Cash", "Debit", "Credit"].map((method, index) => (
      <div key={method} className="d-flex align-items-center mb-2">
        <Form.Check
          type="checkbox"
          id={method}
          label={method}
          checked={!!formData.acceptedPayments[index]}
          onChange={(e) =>
            setFormData((prev) => {
              const updatedPayments = [...prev.acceptedPayments];
              if (e.target.checked) {
                updatedPayments[index] = { method, fee: 0 }; // Add the payment method
              } else {
                updatedPayments[index] = null; // Remove the payment method
              }
              return { ...prev, acceptedPayments: updatedPayments };
            })
          }
        />
        {(method === "Debit" || method === "Credit") && formData.acceptedPayments[index] && (
          <InputGroup className="ms-3" style={{ maxWidth: "100px" }}>
            <InputGroup.Text>$</InputGroup.Text>
            <Form.Control
              type="number"
              maxLength="2"
              placeholder="Fee"
              value={formData.acceptedPayments[index]?.fee || ""}
              onChange={(e) => handlePaymentFeeChange(index, e.target.value.slice(0, 2))}
            />
          </InputGroup>
        )}
      </div>
    ))}
  </div>
</Form.Group>



        <Button variant="primary" onClick={handleSubmit}>
          Register
        </Button>
      </Form>
    </div>
  );
};

export default VendorRegistration;
