import React, { useState, useEffect } from 'react';
import { Dropdown } from 'react-bootstrap';
import './VendorStatusDropdown.css';

const VendorStatusDropdown = ({ vendor }) => {
  const [status, setStatus] = useState('Closed');
  const [availabilityText, setAvailabilityText] = useState('');

  // Helper function to format time as "HH:MM AM/PM"
  const formatTime = (time24) => {
    const [hour, minute] = time24.split(':').map(Number);
    const period = hour >= 12 ? 'PM' : 'AM';
    const formattedHour = hour % 12 || 12; // Convert 0 to 12 for midnight
    return `${formattedHour}:${minute.toString().padStart(2, '0')} ${period}`;
  };

  // Helper function to determine availability text
  const checkStatus = () => {
    const today = new Date();
    const currentDay = today.toLocaleString('en-US', { weekday: 'long' }).toLowerCase();
    const currentTime = today.toTimeString().slice(0, 5); // Current time as "HH:MM"

    const daysOfWeek = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'];

    const { open, close } = vendor.businessHours[currentDay] || {};
    let updatedStatus = 'Closed';
    let updatedText = '';

    if (open && close) {
      if (currentTime >= open && currentTime < close) {
        updatedStatus = 'Available';
        updatedText = `Available until ${formatTime(close)}`;
      } else {
        // Find next day's opening time
        let nextDayIndex = (daysOfWeek.indexOf(currentDay) + 1) % 7;
        while (!vendor.businessHours[daysOfWeek[nextDayIndex]]?.open) {
          nextDayIndex = (nextDayIndex + 1) % 7; // Skip closed days
        }
        const nextOpenTime = vendor.businessHours[daysOfWeek[nextDayIndex]].open;
        updatedText = `Available at ${formatTime(nextOpenTime)}`;
      }
    } else {
      updatedText = 'Closed for today';
    }

    setStatus(updatedStatus);
    setAvailabilityText(updatedText);
  };

  useEffect(() => {
    checkStatus();
    const interval = setInterval(checkStatus, 60000); // Check status every minute
    return () => clearInterval(interval);
  }, [vendor.businessHours]);

  return (
    <Dropdown>
      <Dropdown.Toggle
        variant={status === 'Available' ? 'success' : 'danger'}
        id="vendor-status-dropdown"
        className="mb-3 btn-sm"
      >
        {status}
      </Dropdown.Toggle>

      <Dropdown.Menu className="vendor-status-dropdown-menu p-3 shadow-lg">
        {/* Vendor Actions */}
        <Dropdown.Item disabled={status === 'Closed'} href={`tel:${vendor.contactNumber}`}>
          Call Vendor
        </Dropdown.Item>
        <Dropdown.Item disabled={status === 'Closed'} href={`sms:${vendor.contactNumber}`}>
          Text Vendor
        </Dropdown.Item>
        <Dropdown.Divider />

        {/* Minimum Order */}
        <div className="px-2 mb-2">
          <strong>${vendor.minOrder || 'N/A'} Minimum</strong>
        </div>

        {/* Availability Text */}
        <div className="px-2 text-muted">
          {availabilityText}
        </div>
      </Dropdown.Menu>
    </Dropdown>
  );
};

export default VendorStatusDropdown;
