// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import 'bootstrap-icons/font/bootstrap-icons.css';
import './styles/main.scss';

import Navbar from './components/NavBar';
import VendorPage from './components/VendorPage';
import { CartProvider } from './context/CartContext';
import CheckoutPage from './components/CheckoutPage';
import VendorRegistration from './components/VendorRegistration';
import { AuthProvider } from './context/AuthContext';
import ProtectedRoute from './components/ProtectedRoute';
import Login from './components/Login';
//import ProductList from './components/ProductList';
import TestMenu from './components/TestMenu';
import VendorStorePage from './components/VendorStorePage';
import TestPlaceOrder from './components/TestPlaceOrder';
import OrderConfirmationPage from './components/OrderConfirmationPage';
import TestDashboard from './components/TestDashboard';

require('dotenv').config();



function App() {
  return (
    <AuthProvider> {/* Wrap the entire app in AuthProvider for auth context */}
      <CartProvider>
        <Router>
          <Navbar /> {/* Navbar can access cart and auth context */}
          <Routes>
            {/* Public Routes */}
            <Route path="/" element={<VendorPage />} /> {/* Home - List of vendors */}
            <Route path="/vendor/:vendorId" element={<VendorStorePage />} /> {/* Individual vendor's page */}
            <Route path="/checkout" element={<CheckoutPage />} />
            <Route path="/register" element={<VendorRegistration />} />
            <Route path="/login" element={<Login />} />
            {/*<Route path="/products" element={<ProductList />} />*/}
            <Route path="/menu" element={<TestMenu />} />
            <Route path="/test" element={<TestPlaceOrder />} />
            <Route path="/order-confirmation/:orderId" element={<OrderConfirmationPage />} />




            {/* Protected Routes */}
            <Route element={<ProtectedRoute />}>
              {/* <Route path="/vendor-dashboard" element={<VendorDashboard />} /> */}
              <Route path="/vendor-dashboard" element={<TestDashboard />} />

            </Route>
          </Routes>
        </Router>
      </CartProvider>
    </AuthProvider>
  );
}

export default App;
