import React, { useState } from 'react';

const DeliveryPickupToggle = () => {
  const [selectedOption, setSelectedOption] = useState('delivery'); // State to track selected option

  return (
    <div className="btn-group" role="group" aria-label="Delivery or Pickup toggle">
      {/* Delivery Button */}
      <input
        type="radio"
        className="btn-check"
        name="btnradio"
        id="btnradio1"
        autoComplete="off"
        checked={selectedOption === 'delivery'}
        onChange={() => setSelectedOption('delivery')}
      />
      <label
        className={`btn ${selectedOption === 'delivery' ? 'text-white' : 'text-secondary'}`}
        style={{
          backgroundColor: selectedOption === 'delivery' ? 'black' : 'white',
          border: '1px solid grey',
          padding: '4px 12px', // Reduce padding to decrease height
          fontSize: '14px',    // Adjust font size for better proportions
          fontFamily: 'Poppins, sans-serif', // Set font to Poppins
        }}
        htmlFor="btnradio1"
      >
        Delivery
      </label>

      {/* Pickup Button */}
      <input
        type="radio"
        className="btn-check"
        name="btnradio"
        id="btnradio3"
        autoComplete="off"
        checked={selectedOption === 'pickup'}
        onChange={() => setSelectedOption('pickup')}
      />
      <label
        className={`btn ${selectedOption === 'pickup' ? 'text-white' : 'text-secondary'}`}
        style={{
          backgroundColor: selectedOption === 'pickup' ? 'black' : 'white',
          border: '1px solid grey',
          padding: '4px 12px', // Reduce padding to decrease height
          fontSize: '14px',    // Adjust font size for better proportions
          fontFamily: 'Poppins, sans-serif', // Set font to Poppins
          fontWeight: ''
        }}
        htmlFor="btnradio3"
      >
        Pickup
      </label>
    </div>
  );
};

export default DeliveryPickupToggle;
