import React, { useState, useEffect, useMemo } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';
import VendorCard from './VendorCard';
import api from '../api/api';

const VendorMap = () => {
  const [vendors, setVendors] = useState([]);
  const [selectedVendor, setSelectedVendor] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [mapCenter, setMapCenter] = useState({
    lat: 34.0522,
    lng: -118.2437
  });
  const [userLocation, setUserLocation] = useState(null);
  const [map, setMap] = useState(null);

  const mapStyles = {
    height: "100vh",
    width: "100%"
  };

  // Memoize the Google Maps options
  const mapOptions = useMemo(() => ({
    zoomControl: true,
    streetViewControl: false,
    mapTypeControl: false,
    fullscreenControl: false,
  }), []);

  useEffect(() => {
    const getCurrentLocation = () => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            const userPos = {
              lat: position.coords.latitude,
              lng: position.coords.longitude
            };
            setUserLocation(userPos);
            setMapCenter(userPos);
          },
          (error) => {
            console.error('Geolocation error:', error);
          }
        );
      }
    };

    const fetchVendors = async () => {
        try {
          setLoading(true);
          setError(null);
          const response = await api.get('/api/map/map-vendors');

          if (!response.data.success) {
            console.error('API request was not successful');
            setError('Failed to load vendors. Please try again later.');
            return;
          }

          // Process and validate vendors
          const transformedVendors = response.data.vendors
            // Only keep vendors with storefront coordinates
            .filter(vendor => {
              const hasStorefront = vendor.storefrontAddress?.coordinates?.length === 2;
              if (!hasStorefront) {
                console.warn(`Vendor ${vendor.dispensaryName} missing valid coordinates`);
              }
              return hasStorefront;
            })
            // Transform each vendor
            .map(vendor => {
              // Extract and validate coordinates
              const [lng, lat] = vendor.storefrontAddress.coordinates.map(coord => {
                // Handle MongoDB extended JSON format or plain numbers
                return typeof coord === 'object' ? 
                  parseFloat(coord.$numberDouble || coord.$numberInt) : 
                  parseFloat(coord);
              });

              // Skip vendors with invalid coordinates
              if (isNaN(lat) || isNaN(lng) || 
                  lat < -90 || lat > 90 || 
                  lng < -180 || lng > 180) {
                console.warn(`Vendor ${vendor.dispensaryName} has invalid coordinates: [${lng}, ${lat}]`);
                return null;
              }

              // Return transformed vendor with validated coordinates
              return {
                ...vendor,
                _id: vendor._id.toString(), // Ensure ID is a string
                coordinates: { lat, lng },   // Format for Google Maps
                businessHours: vendor.businessHours || {},
                acceptedPayments: vendor.acceptedPayments || [],
                minOrder: vendor.minOrder || 0,
                products: vendor.products || [],
                dailyPromo: vendor.dailyPromo || { applicableToSaleItems: false }
              };
            })
            .filter(Boolean); // Remove null entries

          // Log the final processed vendors for debugging
          console.log('Processed vendors:', transformedVendors);

          if (transformedVendors.length === 0) {
            console.warn('No valid vendors found after processing');
            setError('No vendors available in your area.');
            return;
          }

          setVendors(transformedVendors);

        } catch (err) {
          console.error('Error fetching vendors:', err);
          setError(err.response?.data?.message || 'Failed to load vendors. Please try again later.');
        } finally {
          setLoading(false);
        }
      };

    getCurrentLocation();
    fetchVendors();
  }, []);

  const handleMapLoad = React.useCallback((mapInstance) => {
    console.log('Map loaded');
    setMap(mapInstance);
  }, []);

  // Render markers only when map is loaded
  const renderMarkers = () => {
    if (!map) {
      console.log('Map not loaded yet');
      return null;
    }

    console.log('Rendering markers for vendors:', vendors);

    return vendors.map(vendor => {
      // Log each vendor's coordinates for debugging
      console.log('Rendering marker for vendor:', {
        name: vendor.dispensaryName,
        id: vendor._id,
        coordinates: vendor.coordinates,
        storefrontAddress: vendor.storefrontAddress
      });

      // Ensure we have valid coordinates
      if (!vendor.coordinates) {
        console.warn(`Missing coordinates for vendor ${vendor.dispensaryName}`);
        return null;
      }

      // Double check coordinate validity
      if (isNaN(vendor.coordinates.lat) || isNaN(vendor.coordinates.lng)) {
        console.warn(`Invalid coordinates for vendor ${vendor.dispensaryName}`);
        return null;
      }

      return (
        <Marker
          key={`marker-${vendor._id}`}
          position={vendor.coordinates}
          onClick={() => {
            console.log('Marker clicked:', vendor.dispensaryName, vendor.coordinates);
            setSelectedVendor(vendor);
            map.panTo(vendor.coordinates);
            map.setZoom(15);
          }}
          icon={{
            url: vendor.logoUrl || '/placeholder-logo.png',
            scaledSize: new window.google.maps.Size(40, 40)
          }}
        />
      );
    }).filter(Boolean);  // Remove any null markers
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-xl">Loading vendors...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-xl text-red-600">{error}</div>
      </div>
    );
  }

  return (
    <div className="flex h-screen">
      {/* Map Section */}
      <div className="w-1/2 h-full relative">
        <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}>
          <GoogleMap
            mapContainerStyle={mapStyles}
            zoom={12}
            center={mapCenter}
            options={mapOptions}
            onLoad={handleMapLoad}
          >
            {map && userLocation && (
              <Marker
                position={userLocation}
                icon={{
                  url: "data:image/svg+xml,%3Csvg width='24' height='24' xmlns='http://www.w3.org/2000/svg'%3E%3Ccircle cx='12' cy='12' r='7' fill='%234285F4' stroke='white' stroke-width='2'/%3E%3C/svg%3E",
                  scaledSize: new window.google.maps.Size(24, 24)
                }}
              />
            )}

            {renderMarkers()}

            {selectedVendor && (
              <InfoWindow
                position={selectedVendor.coordinates}
                onCloseClick={() => setSelectedVendor(null)}
              >
                <div className="p-2">
                  <h3 className="font-semibold text-lg">{selectedVendor.dispensaryName}</h3>
                  <p className="text-sm text-gray-600">{selectedVendor.storefrontAddress.formatted}</p>
                  <p className="text-sm">{selectedVendor.dispensaryType}</p>
                </div>
              </InfoWindow>
            )}
          </GoogleMap>
        </LoadScript>
      </div>

      {/* Vendor Cards Section */}
      <div className="w-1/2 h-full overflow-y-auto bg-gray-50 p-4">
        <h2 className="text-2xl font-semibold mb-4">
          Nearby Dispensaries ({vendors.length})
        </h2>
        <div className="space-y-4">
          {vendors.map(vendor => (
            <VendorCard
              key={`card-${vendor._id.toString()}`}
              vendor={vendor}
              orderType={vendor.dispensaryType.toLowerCase()}
              isSelected={selectedVendor?._id === vendor._id}
              onClick={() => {
                setSelectedVendor(vendor);
                map?.panTo(vendor.coordinates);
              }}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default VendorMap;
