import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuthState] = useState({
        token: null,
        vendorId: null,
        vendorName: null,
    });
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const initializeAuth = async () => {
            const storedAuth = JSON.parse(localStorage.getItem('auth'));
            if (storedAuth?.token) {
                try {
                    // Attempt to refresh the token
                    const response = await axios.post(
                        '/api/auth/refresh-token',
                        {}, // No body needed
                        {
                            headers: {
                                Authorization: `Bearer ${storedAuth.token}`,
                            },
                            withCredentials: true, // Ensure cookies (if used) are sent
                        }
                    );
                    // If successful, update the auth state with the new token
                    const { accessToken } = response.data;
                    const updatedAuth = { ...storedAuth, token: accessToken };
                    setAuth(updatedAuth);
                } catch (error) {
                    console.error('Failed to refresh token:', error);
                    handleLogout();
                }
            }
            setLoading(false); // Finished loading
        };

        initializeAuth();
    }, []);

    const setAuth = (authData) => {
        localStorage.setItem('auth', JSON.stringify(authData));
        setAuthState(authData);
        axios.defaults.headers.common['Authorization'] = `Bearer ${authData.token}`;
    };

    const handleLogout = (navigate = null) => {
        localStorage.removeItem('auth');
        setAuthState({ token: null, vendorId: null, vendorName: null });
        delete axios.defaults.headers.common['Authorization'];
        if (navigate) {
            navigate('/login');
        }
    };

    return (
        <AuthContext.Provider value={{ auth, setAuth, logout: handleLogout, loading }}>
            {!loading ? children : <div>Loading...</div>}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    const context = React.useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};
