import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';
import VendorCard from './VendorCard';

const VendorMap = () => {
  const [vendors, setVendors] = useState([]);
  const [selectedVendor, setSelectedVendor] = useState(null);
  const [mapCenter, setMapCenter] = useState({
    lat: 34.0522,
    lng: -118.2437
  });
  const [userLocation, setUserLocation] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const mapStyles = {
    height: "100vh",
    width: "100%"
  };

  const defaultOptions = {
    panControl: true,
    zoomControl: true,
    mapTypeControl: false,
    scaleControl: true,
    streetViewControl: false,
    rotateControl: false,
    clickableIcons: false,
    keyboardShortcuts: true,
    scrollwheel: true,
    disableDoubleClickZoom: false,
    fullscreenControl: false,
  };

  // Helper function to convert buffer ID to hex string
  const convertBufferToHexString = (buffer) => {
    if (!buffer) return null;
    return Array.from(Object.values(buffer))
      .map(byte => byte.toString(16).padStart(2, '0'))
      .join('');
  };

  // Helper function to get proper vendor ID
  const getVendorId = (vendor) => {
    if (!vendor._id) return null;
    if (vendor._id.buffer) {
      return convertBufferToHexString(vendor._id.buffer);
    }
    return vendor._id.toString();
  };

  // Format vendor data
  const formatVendorData = (vendor) => {
    const id = getVendorId(vendor);
    return {
      ...vendor,
      _id: id,
      coordinates: vendor.storefrontAddress?.coordinates ? {
        lat: parseFloat(vendor.storefrontAddress.coordinates[1]),
        lng: parseFloat(vendor.storefrontAddress.coordinates[0])
      } : null,
      // Ensure all required fields have default values
      minOrder: vendor.minOrder || 0,
      businessHours: vendor.businessHours || {},
      acceptedPayments: vendor.acceptedPayments || [],
      dailyPromo: vendor.dailyPromo || { applicableToSaleItems: false }
    };
  };

  useEffect(() => {
    const getUserLocation = () => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            const location = {
              lat: position.coords.latitude,
              lng: position.coords.longitude
            };
            setUserLocation(location);
            setMapCenter(location);
          },
          (error) => {
            console.error('Geolocation error:', error);
            setError('Unable to get your location. Showing default map view.');
          }
        );
      }
    };

    const fetchVendors = async () => {
      try {
        setLoading(true);
        const response = await fetch('/api/vendors/nearby');
        const data = await response.json();

        if (data.success && Array.isArray(data.vendors)) {
          const formattedVendors = data.vendors
            .map(formatVendorData)
            .filter(vendor => vendor.coordinates && vendor._id);

          setVendors(formattedVendors);
        } else {
          throw new Error('Invalid vendor data received');
        }
      } catch (error) {
        console.error('Error fetching vendors:', error);
        setError('Failed to load vendors. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    getUserLocation();
    fetchVendors();
  }, []);

  if (loading) {
    return <div className="flex h-screen items-center justify-center">Loading...</div>;
  }

  if (error) {
    return <div className="flex h-screen items-center justify-center text-red-500">{error}</div>;
  }

  return (
    <div className="flex h-screen">
      {/* Map Section */}
      <div className="w-1/2 h-full relative">
        <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}>
          <GoogleMap
            mapContainerStyle={mapStyles}
            zoom={12}
            center={mapCenter}
            options={defaultOptions}
          >
            {/* User Location Marker */}
            {userLocation && (
              <Marker
                position={userLocation}
                icon={{
                  url: "data:image/svg+xml,%3Csvg width='24' height='24' xmlns='http://www.w3.org/2000/svg'%3E%3Ccircle cx='12' cy='12' r='7' fill='%234285F4' stroke='white' stroke-width='2'/%3E%3C/svg%3E",
                  scaledSize: { width: 24, height: 24 }
                }}
              />
            )}

            {/* Vendor Markers */}
            {vendors.map(vendor => (
              <Marker
                key={vendor._id}
                position={vendor.coordinates}
                onClick={() => setSelectedVendor(vendor)}
                icon={{
                  url: vendor.logoUrl || '/default-marker.png',
                  scaledSize: { width: 40, height: 40 }
                }}
              />
            ))}

            {/* Selected Vendor Info Window */}
            {selectedVendor && (
              <InfoWindow
                position={selectedVendor.coordinates}
                onCloseClick={() => setSelectedVendor(null)}
              >
                <div className="p-2">
                  <h3 className="font-semibold text-lg">{selectedVendor.dispensaryName}</h3>
                  <p className="text-sm text-gray-600">{selectedVendor.storefrontAddress.formatted}</p>
                  <p className="text-sm mt-1">{selectedVendor.dispensaryType}</p>
                  {selectedVendor.minOrder > 0 && (
                    <p className="text-sm">Min. Order: ${selectedVendor.minOrder}</p>
                  )}
                </div>
              </InfoWindow>
            )}
          </GoogleMap>
        </LoadScript>
      </div>

      {/* Vendor Cards Section */}
      <div className="w-1/2 h-full overflow-y-auto bg-gray-50 p-4">
        <h2 className="text-2xl font-semibold mb-4">Nearby Dispensaries</h2>
        <div className="space-y-4">
          {vendors.map(vendor => (
            <div
              key={vendor._id}
              className={`transition-all duration-200 ${
                selectedVendor?._id === vendor._id ? 'ring-2 ring-blue-500' : ''
              }`}
              onClick={() => {
                setSelectedVendor(vendor);
                setMapCenter(vendor.coordinates);
              }}
            >
              <VendorCard
                vendor={vendor}
                orderType={vendor.dispensaryType.toLowerCase()}
              />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default VendorMap;
