import React, { useState, useContext, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button, Row, Col, Form, Card, Container } from 'react-bootstrap';
import { CartContext } from '../context/CartContext';

const TestPlaceOrder = () => {
  const { calculateTotal, cartItems, clearCart, incrementItem, decrementItem, removeFromCart } = useContext(CartContext);

  const [contactInfo, setContactInfo] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    birthdate: '',
  });

  const [savedAddress, setSavedAddress] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [newAddress, setNewAddress] = useState('');
  const [paymentMethod, setPaymentMethod] = useState('');
  const [vendorPaymentMethods, setVendorPaymentMethods] = useState([]);
  const [vendorName, setVendorName] = useState('vendor');
  const inputRef = useRef(null);

  const navigate = useNavigate();
  const subtotal = calculateTotal();
  const estimatedTotal = subtotal;

  // Load Google Maps API
  useEffect(() => {
    const loadGoogleMapsScript = () => {
      if (!window.google) {
        const script = document.createElement('script');
        script.src = `https://maps.googleapis.com/maps/api/js?key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}&libraries=places`;
        script.async = true;
        script.defer = true;
        script.onload = () => {
          console.log('Google Maps script loaded successfully.');
        };
        script.onerror = () => {
          console.error('Error loading Google Maps script.');
        };
        document.head.appendChild(script);
      }
    };

    loadGoogleMapsScript();
  }, []);

  useEffect(() => {
    const address = localStorage.getItem('userAddress');
    if (address) setSavedAddress(address);

    const storedContactInfo = JSON.parse(localStorage.getItem('contactInfo'));
    if (storedContactInfo) setContactInfo(storedContactInfo);

    const fetchVendorData = async () => {
      const vendorId = cartItems[0]?.vendorId;
      if (vendorId) {
        try {
          const response = await fetch(`http://localhost:5001/api/vendors/${vendorId}`);
          const vendor = await response.json();
          setVendorPaymentMethods(vendor.data.acceptedPayments || []);
          setVendorName(vendor.data.dispensaryName || 'vendor');
        } catch (error) {
          console.error('Error fetching vendor data:', error);
        }
      }
    };

    fetchVendorData();
  }, [cartItems]);

  useEffect(() => {
    if (isEditing && inputRef.current && window.google?.maps?.places) {
      const autocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
        types: ['geocode'],
      });

      autocomplete.addListener('place_changed', () => {
        const place = autocomplete.getPlace();
        if (place?.formatted_address) {
          setNewAddress(place.formatted_address);
        }
      });
    }
  }, [isEditing]);

  useEffect(() => {
    localStorage.setItem('contactInfo', JSON.stringify(contactInfo));
  }, [contactInfo]);

  const formatBirthdate = (value) => {
    const digits = value.replace(/\D/g, '');
    const parts = [
      digits.slice(0, 2),
      digits.slice(2, 4),
      digits.slice(4, 8),
    ].filter(Boolean);
    return parts.join('/');
  };

  const handleBirthdateChange = (e) => {
    const formattedValue = formatBirthdate(e.target.value);
    setContactInfo({ ...contactInfo, birthdate: formattedValue });
  };

  const handleSaveAddress = () => {
    localStorage.setItem('userAddress', newAddress);
    setSavedAddress(newAddress);
    setIsEditing(false);
  };

  const handlePlaceOrder = async () => {
    // Validation for required fields
    if (!contactInfo.firstName || !contactInfo.lastName || !contactInfo.email || !contactInfo.phone || !contactInfo.birthdate) {
      alert('Please fill out all required fields.');
      return;
    }
    if (!savedAddress) {
      alert('Please add a delivery address.');
      return;
    }
    if (!paymentMethod) {
      alert('Please select a payment method.');
      return;
    }

    // Prepare the payload with savedAddress as deliveryAddress
    const payload = {
      phone: contactInfo.phone,
      firstName: contactInfo.firstName,
      lastName: contactInfo.lastName,
      email: contactInfo.email,
      birthday: contactInfo.birthdate,
      totalAmount: estimatedTotal,
      vendorId: cartItems[0]?.vendorId,
      orderDetails: {
        type: 'DELIVERY',
        order_source: 'ECOMMERCE',
        order_status: 'AWAITING_PROCESSING',
        deliveryddress: savedAddress, // Include the saved address
        items: cartItems.map((item) => ({
          productId: item.productId || item._id,
          name: item.name,
          category: item.category,
          quantity: item.quantity,
          price: item.price, // Ensure price is included
        })),
        payment_method: paymentMethod,
      },
    };

    try {
      const response = await fetch('http://localhost:5001/api/customer/create-order', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      if (response.ok) {
        const result = await response.json();
        navigate(`/order-confirmation/${result.orderId}`, {
          state: {
            order: result.order,
            cartItemsSummary: result.order.items, // Pass items to the next page
          },
        });
        clearCart();
      } else {
        const error = await response.json();
        alert(`Failed to place order: ${error.message}`);
      }
    } catch (error) {
      console.error('Error placing order:', error);
      alert('An error occurred. Please try again later.');
    }
  };



  return (
    <div>
      <Container fluid className="place-order-section py-4">
        <Row className="g-4">
          <Col lg={6} xs={12}>
            <div className="p-0" style={{ backgroundColor: '#EFEFEF' }}>
              <Card>
                <Card.Body>
                  <h4>Delivery Address</h4>
                  {isEditing ? (
                    <Form>
                      <Form.Group controlId="address" className="mb-3">
                        <Form.Label>Delivery Address</Form.Label>
                        <Form.Control
                          type="text"
                          ref={inputRef}
                          value={newAddress}
                          onChange={(e) => setNewAddress(e.target.value)}
                          placeholder="Enter your address"
                          required
                        />
                      </Form.Group>
                      <Button variant="success" onClick={handleSaveAddress}>
                        Save Address
                      </Button>
                    </Form>
                  ) : (
                    <div>
                      <Card.Text className="text-secondary text-sm">
                        {savedAddress || 'N/A'}
                      </Card.Text>
                      <Button
                        variant="primary"
                        onClick={() => {
                          setNewAddress(savedAddress);
                          setIsEditing(true);
                        }}
                      >
                        Edit
                      </Button>
                    </div>
                  )}
                  <hr />
                  <h4>Contact Information</h4>
                  <Form>
                    <Row className="mb-3">
                      <Col>
                        <Form.Label>First Name</Form.Label>
                        <Form.Control
                          type="text"
                          value={contactInfo.firstName}
                          onChange={(e) =>
                            setContactInfo({ ...contactInfo, firstName: e.target.value })
                          }
                          required
                        />
                      </Col>
                      <Col>
                        <Form.Label>Last Name</Form.Label>
                        <Form.Control
                          type="text"
                          value={contactInfo.lastName}
                          onChange={(e) =>
                            setContactInfo({ ...contactInfo, lastName: e.target.value })
                          }
                          required
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Col>
                        <Form.Label>Email</Form.Label>
                        <Form.Control
                          type="email"
                          value={contactInfo.email}
                          onChange={(e) =>
                            setContactInfo({ ...contactInfo, email: e.target.value })
                          }
                          required
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Col>
                        <Form.Label>Phone</Form.Label>
                        <Form.Control
                          type="text"
                          value={contactInfo.phone}
                          placeholder=""
                          onChange={(e) => {
                            const input = e.target.value.replace(/\D/g, '');
                            let formattedInput = input;

                            if (input.length > 3 && input.length <= 6) {
                              formattedInput = `(${input.slice(0, 3)}) ${input.slice(3)}`;
                            } else if (input.length > 6) {
                              formattedInput = `(${input.slice(0, 3)}) ${input.slice(3, 6)}-${input.slice(6, 10)}`;
                            }

                            setContactInfo({ ...contactInfo, phone: formattedInput });
                          }}
                          maxLength={14}
                          required
                        />
                      </Col>
                    </Row>
                    <Row className="mb-3">
                      <Col>
                        <Form.Label>Birthdate</Form.Label>
                        <Form.Control
                          type="text"
                          placeholder="MM/DD/YYYY"
                          value={contactInfo.birthdate}
                          onChange={handleBirthdateChange}
                          required
                        />
                      </Col>
                    </Row>
                  </Form>
                  <p>Select Payment Method:</p>
                  <div className="d-flex flex-wrap">
                    {vendorPaymentMethods.map((method, index) => (
                      <Button
                        key={index}
                        variant={paymentMethod === method.method ? 'primary' : 'outline-primary'}
                        className="m-2"
                        onClick={() => setPaymentMethod(method.method)}
                      >
                        {method.fee > 0
                          ? `${method.method} ($${method.fee.toFixed(2)} Fee)`
                          : method.method}
                      </Button>
                    ))}
                  </div>
                </Card.Body>
              </Card>
            </div>
          </Col>

          {/* Right Container: Order Summary and Place Order */}
          <Col lg={6} xs={12}>
            <div className="p-0" style={{ backgroundColor: '#EFEFEF' }}>
            <Card>
  <Card.Body>
    <h4>Order Summary</h4>
    {cartItems.length > 0 ? (
      cartItems.map((item) => (
        <div
          key={item.productId}
          className="d-flex align-items-center mb-3"
          style={{ borderBottom: '1px solid #e0e0e0', paddingBottom: '10px' }}
        >
          {/* Product Image */}
          <img
            src={item.image}
            alt={item.name}
            style={{ width: '50px', height: '50px', objectFit: 'cover', marginRight: '10px' }}
          />

          {/* Product Details */}
          <div className="flex-grow-1">
            <strong>{item.name}</strong>
            <div>
              {item.salePrice ? (
                <div>
                  <span className="text-danger me-2">
                    ${item.salePrice.toFixed(2)}
                  </span>
                  <span className="text-muted text-decoration-line-through">
                    ${item.price.toFixed(2)}
                  </span>
                  <div className="text-success small">
                    ${Math.abs(item.price - item.salePrice).toFixed(2)} Savings!
                  </div>
                </div>
              ) : (
                <span>${item.price.toFixed(2)}</span>
              )}
            </div>
          </div>

          {/* Quantity and Increment/Decrement Buttons */}
          <div className="d-flex align-items-center">
            <button
              className="btn btn-outline-secondary btn-sm"
              style={{ width: '30px', height: '30px', fontSize: '1rem' }}
              onClick={() => decrementItem(item.productId)}
            >
              -
            </button>
            <span className="mx-2">{item.quantity}</span>
            <button
              className="btn btn-outline-secondary btn-sm"
              style={{ width: '30px', height: '30px', fontSize: '1rem' }}
              onClick={() => incrementItem(item.productId)}
            >
              +
            </button>
          </div>

          {/* Trash Icon */}
          <button
            className="btn btn-outline-danger btn-sm ms-2"
            style={{ width: '30px', height: '30px', fontSize: '1rem', border: '0' }}
            onClick={() => removeFromCart(item.productId)}
          >
            <i className="bi bi-trash"></i>
          </button>
        </div>
      ))
    ) : (
      <p>Your cart is empty.</p>
    )}

    {/* Total Price */}
    <div className="d-flex justify-content-between">
      <span>
        <strong>Total</strong> <span className="text-secondary" style={{ fontSize: '0.8rem' }}>(Taxes Included)</span>
      </span>
      <span>${estimatedTotal.toFixed(2)}</span>
    </div>

    {/* Place Order Button */}
    <Button variant="primary" className="w-100 mt-4" onClick={handlePlaceOrder}>
      Place Order
    </Button>
    <p className="text-secondary text-center mt-3" style={{ fontSize: '0.9rem' }}>
      Please note, you will receive a text message from {vendorName} with delivery ETA shortly after placing order.
    </p>
  </Card.Body>
</Card>

            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default TestPlaceOrder;
