import React, { useState } from 'react';
import { Navbar, Container, Image } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import VendorLogoCard from './VendorLogoCard';
import VendorInfoCard from './VendorInfoCard';
import DeactivateAccount from './DeactivateAccount';

const DashNavBar = () => {
  const [showSidebar, setShowSidebar] = useState(false);
  const [vendorData, setVendorData] = useState(null);
  const navigate = useNavigate();

  React.useEffect(() => {
    const fetchVendorData = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/vendors/profile', {
          withCredentials: true,
        });
        setVendorData(response.data.data);
      } catch (error) {
        console.error('Error fetching vendor data:', error);
      }
    };
    fetchVendorData();
  }, []);

  const handleClose = () => {
    setShowSidebar(false);
  };

  const handleLogoClick = () => {
    if (vendorData?._id) {
      navigate(`/vendor/${vendorData._id}/menu`);
    }
  };

  // Style objects
  const navbarStyle = {
    backgroundColor: '#1a1a1a',
    height: '60px',
  };

  const logoStyle = {
    height: '35px',
    width: 'auto',
    borderRadius: '6px',
    cursor: 'pointer'
  };

  const vendorLogoStyle = {
    height: '40px',
    width: '40px',
    borderRadius: '50%',
    cursor: 'pointer',
    objectFit: 'cover',
  };

  const dashboardTextStyle = {
    color: '#ffffff',
    fontSize: '0.9rem',
    margin: '0',
    fontWeight: '400',
    position: 'absolute',
    left: '50%',
    transform: 'translateX(-50%)',
  };

  const sidebarStyle = {
    position: 'fixed',
    right: showSidebar ? '0' : '-320px',
    top: '0',
    width: '320px',
    height: '100vh',
    backgroundColor: 'white',
    boxShadow: '-2px 0 5px rgba(0, 0, 0, 0.2)',
    transition: 'right 0.3s ease-in-out',
    zIndex: 1040,
    overflowY: 'auto',
  };

  const overlayStyle = {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    zIndex: 1039,
    display: showSidebar ? 'block' : 'none',
  };

  return (
    <>
      <Navbar style={navbarStyle} variant="dark" fixed="top">
        <Container fluid className="position-relative">
          <Navbar.Brand>
            <Image
              src={require('../assets/images/miniLogo.jpg')}
              alt="Mini Logo"
              style={logoStyle}
              onClick={handleLogoClick}
            />
          </Navbar.Brand>
          <span style={dashboardTextStyle}>Dashboard</span>
          <Image
            src={vendorData?.logoUrl || '/placeholder-profile.png'}
            alt="Vendor Logo"
            style={vendorLogoStyle}
            onClick={() => setShowSidebar(true)}
          />
        </Container>
      </Navbar>

      {/* Overlay */}
      <div style={overlayStyle} onClick={handleClose} />

      {/* Sidebar */}
      <div style={sidebarStyle}>
        {showSidebar && (
          <div className="p-4">
            <div className="d-flex justify-content-between align-items-center mb-4">
              <h5 className="mb-0">Profile</h5>
              <button
                className="btn btn-link text-dark"
                onClick={handleClose}
              >
                Ã—
              </button>
            </div>

            {/* Vendor Components */}
            <div className="sidebar-content">
              <div className="mb-4">
                <VendorLogoCard />
              </div>

              <div className="mb-4">
                <VendorInfoCard />
              </div>

              <div className="text-center mt-5">
                <DeactivateAccount />
              </div>
            </div>
          </div>
        )}
      </div>
    </>
  );
};

export default DashNavBar;
