import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [auth, setAuth] = useState(() => {
        const storedAuth = JSON.parse(localStorage.getItem('auth'));
        return storedAuth || null;
    });
    const [loading, setLoading] = useState(!auth);

    const login = (authData) => {
        setAuth(authData);
        localStorage.setItem('auth', JSON.stringify(authData));
    };

    const logout = () => {
        setAuth(null);
        localStorage.removeItem('auth');
        window.location.href = '/login';
    };

    const refreshAccessToken = async () => {
        try {
            const storedAuth = JSON.parse(localStorage.getItem('auth'));
            if (!storedAuth?.refreshToken) throw new Error('No refresh token available');

            const response = await axios.post('http://localhost:5001/api/auth/refresh-token', {}, {
                headers: { Authorization: `Bearer ${storedAuth.refreshToken}` },
            });

            const updatedAuth = {
                ...storedAuth,
                token: response.data.accessToken,
                refreshToken: response.data.refreshToken,
            };

            setAuth(updatedAuth);
            localStorage.setItem('auth', JSON.stringify(updatedAuth));
            return updatedAuth.token;
        } catch (error) {
            console.error('Failed to refresh token:', error);
            logout(); // Log the user out on failure
            throw new Error('Unable to refresh token');
        }
    };

    useEffect(() => {
        if (!auth) setLoading(false);
    }, [auth]);

    return (
        <AuthContext.Provider value={{ auth, setAuth, login, logout, refreshAccessToken, loading }}>
            {loading ? <div>Loading...</div> : children}
        </AuthContext.Provider>
    );
};



// Create a custom hook for accessing the AuthContext
export const useAuth = () => {
    return useContext(AuthContext);
};
