import React, { useState, useEffect } from 'react';
import { Card, Button, Modal, Form, Badge } from 'react-bootstrap';
import axios from 'axios';

const DispensaryInfoCard = () => {
    const [dispensary, setDispensary] = useState({});
    const [showModal, setShowModal] = useState(false);
    const [formData, setFormData] = useState({
        dispensaryName: '',
        dispensaryType: '',
        license: '',
    });
    const [alert, setAlert] = useState({ message: '', variant: '' });

    useEffect(() => {
        const fetchDispensaryData = async () => {
            try {
                const response = await axios.get('http://localhost:5001/api/vendors/profile', {
                    withCredentials: true,
                });
                setDispensary(response.data.data);
            } catch (error) {
                console.error('Error fetching dispensary details:', error);
                setAlert({ message: 'Failed to load dispensary details.', variant: 'danger' });
            }
        };
        fetchDispensaryData();
    }, []);

    const handleEdit = () => {
        setFormData({
            dispensaryName: dispensary.dispensaryName || '',
            dispensaryType: dispensary.dispensaryType || '',
            license: dispensary.license || '',
        });
        setShowModal(true);
    };

    const handleTypeChange = (type) => {
        setFormData(prev => ({ ...prev, dispensaryType: type }));
    };

    const handleUpdate = async () => {
        try {
            const response = await axios.put(
                'http://localhost:5001/api/vendors/dispensary-info',
                formData,
                { withCredentials: true }
            );

            if (response.data.success) {
                setDispensary(response.data.data);
                setAlert({ message: 'Dispensary information updated successfully!', variant: 'success' });
                setShowModal(false);
            }
        } catch (error) {
            console.error('Error updating dispensary details:', error);
            setAlert({ message: 'Failed to update details.', variant: 'danger' });
        }
    };

    const dispensaryTypes = ['Delivery', 'Pickup', 'Pickup & Delivery'];

    const modalStyle = {
        zIndex: '1050',
    };

    return (
        <div>
            <Card className="shadow-sm" style={{ width: '100%' }}>
                <Card.Body className="p-3">
                    <div className="d-flex justify-content-between align-items-start">
                        <div>
                            <Card.Title className="fw-bold mb-2">
                                {dispensary.dispensaryName}
                            </Card.Title>
                            <Badge bg="light" text="dark" className="border mb-2">
                                {dispensary.dispensaryType || 'Type N/A'}
                            </Badge>
                            <div className="text-muted small mt-1">
                                License: {dispensary.license || 'LICENSE N/A'}
                            </div>
                        </div>
                        <Button variant="outline-secondary" size="sm" onClick={handleEdit}>
                            Edit
                        </Button>
                    </div>
                </Card.Body>
            </Card>

            <Modal 
                show={showModal} 
                onHide={() => setShowModal(false)} 
                centered
                style={modalStyle}
            >
                <Modal.Header closeButton>
                    <Modal.Title>Edit Dispensary Information</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group className="mb-3">
                            <Form.Label>Dispensary Name</Form.Label>
                            <Form.Control
                                type="text"
                                value={formData.dispensaryName}
                                onChange={(e) => setFormData(prev => ({ 
                                    ...prev, 
                                    dispensaryName: e.target.value 
                                }))}
                                placeholder="Enter dispensary name"
                            />
                        </Form.Group>

                        <Form.Group className="mb-3">
                            <Form.Label>Dispensary Type</Form.Label>
                            <div>
                                {dispensaryTypes.map((type) => (
                                    <Form.Check
                                        key={type}
                                        type="radio"
                                        label={type}
                                        name="dispensaryType"
                                        checked={formData.dispensaryType === type}
                                        onChange={() => handleTypeChange(type)}
                                        className="mb-2"
                                    />
                                ))}
                            </div>
                        </Form.Group>

                        <Form.Group className="mb-3">
                            <Form.Label>License Number</Form.Label>
                            <Form.Control
                                type="text"
                                value={formData.license}
                                onChange={(e) => setFormData(prev => ({ 
                                    ...prev, 
                                    license: e.target.value 
                                }))}
                                placeholder="Enter license number"
                            />
                        </Form.Group>
                    </Form>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={() => setShowModal(false)}>
                        Cancel
                    </Button>
                    <Button variant="primary" onClick={handleUpdate}>
                        Update
                    </Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
};

export default DispensaryInfoCard;
