import React, { useState, useEffect } from 'react';
import { Offcanvas, ListGroup, Badge, Button, Form } from 'react-bootstrap';
import axios from 'axios';
import { useAuth } from '../context/AuthContext'; // Assuming you're using AuthContext for vendorId

const InventoryComponent = () => {
  const { auth } = useAuth(); // Retrieve vendorId from context
  const [inventory, setInventory] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [showSlideIn, setShowSlideIn] = useState(false);
  const [updatedDetails, setUpdatedDetails] = useState({ price: '', stock: '' });


  useEffect(() => {
    const fetchInventory = async () => {
        try {
          const response = await axios.get(`/api/vendor/inventory/${auth.vendorId}`);
          const data = response.data.map((item) => ({
            ...item,
            price: item.price || 0, // Default to 0 if missing
            stock: item.stock || 0, // Default to 0 if missing
          }));
          setInventory(data);
        } catch (error) {
          console.error('Error fetching inventory:', error);
        }
      };

    fetchInventory();
    }, [auth.vendorId]);


  const handleRowClick = (product) => {
    setSelectedProduct(product);
    setUpdatedDetails({ price: product.price, stock: product.stock });
    setShowSlideIn(true);
  };

  const handleUpdate = async () => {
    try {
      await axios.put(`/api/vendor/inventory/${selectedProduct._id}`, updatedDetails, {
        headers: { Authorization: `Bearer ${auth.token}` },
      });
      // Update local inventory state
      setInventory((prevInventory) =>
        prevInventory.map((item) =>
          item._id === selectedProduct._id
            ? { ...item, price: updatedDetails.price, stock: updatedDetails.stock }
            : item
        )
      );
      setShowSlideIn(false);
    } catch (error) {
      console.error('Error updating product details:', error);
    }
  };

  return (
    <div>
      <h2 className="mb-4">Inventory</h2>
      <ListGroup>
        {inventory.map((product) => (
          <ListGroup.Item
            key={product._id}
            className="d-flex justify-content-between align-items-center"
            style={{ cursor: 'pointer' }}
            onClick={() => handleRowClick(product)}
          >
            {/* Thumbnail and Details */}
            <div className="d-flex align-items-center">
              <img
                src={product.productId.imageUrl}
                alt={product.productId.name}
                className="me-3"
                style={{ width: '50px', height: '50px', objectFit: 'cover' }}
              />
              <div>
                <div className="fw-bold">{product.productId.name}</div>
                <div className="text-muted">{product.productId.strain || 'No Strain'}</div>
              </div>
            </div>
            {/* Price and Arrow */}
            <div className="d-flex align-items-center">
              <span className="fw-bold">${product.price.toFixed(2)}</span>
              <i className="bi bi-chevron-right ms-2 fs-4"></i>
            </div>
          </ListGroup.Item>
        ))}
      </ListGroup>

      {/* Slide-in Window for Product Details */}
      {selectedProduct && (
        <Offcanvas show={showSlideIn} onHide={() => setShowSlideIn(false)} placement="end">
          <Offcanvas.Header closeButton>
            <Offcanvas.Title>Update Product</Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>
            <div>
              <h5>{selectedProduct.productId.name}</h5>
              <p className="text-muted">{selectedProduct.productId.description || 'No Description'}</p>
              <img
                src={selectedProduct.productId.imageUrl}
                alt={selectedProduct.productId.name}
                style={{ width: '100%', height: '200px', objectFit: 'cover' }}
              />
            </div>
            <Form className="mt-4">
              <Form.Group className="mb-3">
                <Form.Label>Price</Form.Label>
                <Form.Control
                  type="number"
                  value={updatedDetails.price}
                  onChange={(e) => setUpdatedDetails({ ...updatedDetails, price: e.target.value })}
                  placeholder="Enter price"
                />
              </Form.Group>
              <Form.Group className="mb-3">
                <Form.Label>Stock</Form.Label>
                <Form.Control
                  type="number"
                  value={updatedDetails.stock}
                  onChange={(e) => setUpdatedDetails({ ...updatedDetails, stock: e.target.value })}
                  placeholder="Enter stock"
                />
              </Form.Group>
              <Button variant="primary" onClick={handleUpdate}>
                Save Changes
              </Button>
            </Form>
          </Offcanvas.Body>
        </Offcanvas>
      )}
    </div>
  );
};

export default InventoryComponent;
