import React, { useEffect, useState, useRef } from 'react';
import VendorCard from './VendorCard';
import PickupMap from './PickupMap';

const NearbyPickupPage = ({ searchQuery }) => {
  const [vendors, setVendors] = useState([]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [isExpanded, setIsExpanded] = useState(false);
  const [isDragging, setIsDragging] = useState(false);
  const [startY, setStartY] = useState(0);
  const [currentY, setCurrentY] = useState(0);

  const vendorCardsRef = useRef(null);
  const dragStartRef = useRef(0);
  const lastScrollPosition = useRef(0);
  const BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:5001/api';

  const calculateDistance = (lat1, lng1, lat2, lng2) => {
    const toRadians = (degree) => (degree * Math.PI) / 180;
    const R = 3963; // Earth's radius in miles
    const dLat = toRadians(lat2 - lat1);
    const dLng = toRadians(lng2 - lng1);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(toRadians(lat1)) * Math.cos(toRadians(lat2)) * Math.sin(dLng / 2) * Math.sin(dLng / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    return R * c;
  };

  const fetchVendors = async () => {
    try {
      const geocodeResponse = await fetch(
        `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(
          localStorage.getItem('userAddress')
        )}&key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}`
      );
      const geocodeData = await geocodeResponse.json();
      if (geocodeData.status !== 'OK') throw new Error('Could not fetch location.');

      const { lat, lng } = geocodeData.results[0].geometry.location;

      const vendorsResponse = await fetch(`${BASE_URL}/vendors/nearby?lat=${lat}&lng=${lng}`);
      const vendorsData = await vendorsResponse.json();

      if (!vendorsData.vendors || !vendorsData.vendors.length) {
        throw new Error('No vendors found near your location.');
      }

      // Filter for Pickup and Pickup & Delivery vendors
      const pickupVendors = vendorsData.vendors.filter(
        vendor => ['Pickup', 'Pickup & Delivery'].includes(vendor.dispensaryType)
      );

      const enrichedVendors = pickupVendors.map((vendor) => {
        // Vendor coordinates are now in the expected format from the backend
        const [vLng, vLat] = vendor.coordinates;
        return {
          ...vendor,
          milesAway: calculateDistance(lat, lng, vLat, vLng)
        };
      });

      setVendors(enrichedVendors);
    } catch (error) {
      console.error('Error fetching vendors:', error);
      setError(error.message || 'Failed to load vendors.');
    }
  };


  const handleMarkerClick = (vendor) => {
    setVendors((prevVendors) => {
      const filteredVendors = prevVendors.filter((v) => v._id !== vendor._id);
      return [vendor, ...filteredVendors]; // Bring clicked vendor to the top
    });
  };

  const filteredVendors = vendors
    .filter((vendor) =>
      // Show both Pickup and Pickup & Delivery vendors
      vendor.dispensaryType === 'Pickup' || vendor.dispensaryType === 'Pickup & Delivery'
    )
    .map((vendor) => {
      const filteredProducts = vendor.products?.filter((product) => {
        const matchesProductName = product.name?.toLowerCase().includes(searchQuery.trim().toLowerCase());
        const matchesBrand = product.brand?.toLowerCase().includes(searchQuery.trim().toLowerCase());
        const matchesStrain = product.strain?.toLowerCase().includes(searchQuery.trim().toLowerCase());
        return matchesProductName || matchesBrand || matchesStrain;
      });

      // Include all vendors but limit products to matches
      return { ...vendor, products: filteredProducts || [] };
    });

  useEffect(() => {
    fetchVendors();
  }, []);

  const handleDragHandleClick = () => {
    setIsExpanded(!isExpanded);
  };

  const handleScroll = (e) => {
    const currentScroll = e.target.scrollTop;
    const scrollingDown = currentScroll > lastScrollPosition.current;
    const nearBottom = e.target.scrollHeight - e.target.scrollTop <= e.target.clientHeight + 100;

    if (scrollingDown && nearBottom && !isExpanded) {
      setIsExpanded(true);
    }

    lastScrollPosition.current = currentScroll;
  };

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartY(e.clientY);
    dragStartRef.current = e.clientY;
    document.body.style.cursor = 'grabbing';
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;

    const deltaY = dragStartRef.current - e.clientY;
    const containerHeight = window.innerHeight;
    const maxDrag = containerHeight * 0.7; // Maximum drag is 70% of screen height

    // Calculate new position with bounds
    const newY = Math.max(0, Math.min(maxDrag, deltaY));
    setCurrentY(newY);

    // Update container position smoothly
    if (vendorCardsRef.current) {
      vendorCardsRef.current.style.transform = `translateY(-${newY}px)`;
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
    document.body.style.cursor = 'default';

    // Determine if we should snap to expanded or collapsed state
    const threshold = window.innerHeight * 0.3; // 30% threshold for expansion
    const shouldExpand = currentY > threshold;

    setIsExpanded(shouldExpand);

    // Animate to final position
    if (vendorCardsRef.current) {
      vendorCardsRef.current.style.transition = 'transform 0.3s ease-out';
      vendorCardsRef.current.style.transform = shouldExpand 
        ? 'translateY(-70vh)' 
        : 'translateY(0)';

      // Reset transition after animation
      setTimeout(() => {
        vendorCardsRef.current.style.transition = '';
      }, 300);
    }
  };

  // Add event listeners for mouse events
  useEffect(() => {
    if (isDragging) {
      window.addEventListener('mousemove', handleMouseMove);
      window.addEventListener('mouseup', handleMouseUp);
    }

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isDragging]);

  // Filter vendors based on search query (existing code)...

  return (
    <div className="container-fluid p-0 pickup-page-container">
      <div className={`map-container ${isExpanded ? 'expanded' : ''}`}>
        <PickupMap
          dispensaries={vendors.map((vendor) => ({
            ...vendor,
            location: vendor.coordinates,
          }))}
          onMarkerClick={handleMarkerClick}
        />
      </div>

      <div
        className={`vendor-card-container ${isExpanded ? 'expanded' : ''} ${isDragging ? 'dragging' : ''}`}
        ref={vendorCardsRef}
      >
        <div
          className="drag-handle"
          onMouseDown={handleMouseDown}
        />

        {filteredVendors.length > 0 ? (
          <div className="vendor-cards-wrapper">
            {filteredVendors.map((vendor) => (
              <VendorCard key={vendor._id} vendor={vendor} />
            ))}
          </div>
        ) : (
          <p className="text-center text-muted my-4">No products match your search query.</p>
        )}
      </div>

      <style jsx>{`
        .pickup-page-container {
          height: 100vh;
          overflow: hidden;
          position: relative;
        }

        .map-container {
          height: 100%;
          transition: height 0.3s ease-out;
        }

        .map-container.expanded {
          height: 30vh;
        }

        .vendor-card-container {
          position: absolute;
          bottom: 0;
          left: 0;
          right: 0;
          background: white;
          border-radius: 16px 16px 0 0;
          height: 70vh;
          transform: translateY(0);
          touch-action: none;
          user-select: none;
        }

        .vendor-card-container.dragging {
          transition: none;
          cursor: grabbing;
        }

        .drag-handle {
          width: 100%;
          height: 24px;
          background: transparent;
          position: relative;
          cursor: grab;
          border-radius: 16px 16px 0 0;
        }

        .drag-handle::before {
          content: '';
          position: absolute;
          top: 8px;
          left: 50%;
          transform: translateX(-50%);
          width: 40px;
          height: 4px;
          background: #CBD5E0;
          border-radius: 2px;
        }

        .vendor-cards-wrapper {
          height: calc(100% - 24px);
          overflow-y: auto;
          padding: 16px;
        }
      `}</style>
    </div>
  );
};

export default NearbyPickupPage;
