import React, { useEffect, useState } from 'react';
import ChangeAddressLink from './ChangeAddressLink';
import VendorCard from './VendorCard';
import { Form } from 'react-bootstrap'; // Import Bootstrap Form for the search bar
import { GeoAltFill } from 'react-bootstrap-icons'; // Import Bootstrap Icons

const NearbyVendorsPage = () => {
  const [vendors, setVendors] = useState([]);
  const [address] = useState(localStorage.getItem('userAddress') || ''); // Address from localStorage
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState(''); // State for search query

  // Base API URL from environment variables
  const BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:5001/api';

  const fetchVendors = async () => {
    if (!address) {
      setError('Please provide a valid address.');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const geocodeResponse = await fetch(
        `https://maps.googleapis.com/maps/api/geocode/json?address=${encodeURIComponent(
          address
        )}&key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}`
      );

      const geocodeData = await geocodeResponse.json();
      if (geocodeData.status !== 'OK') throw new Error('Could not fetch location for the given address.');

      const { lat, lng } = geocodeData.results[0].geometry.location;

      const vendorsResponse = await fetch(`${BASE_URL}/vendors/nearby?lat=${lat}&lng=${lng}`);
      const vendorsData = await vendorsResponse.json();

      if (!vendorsData.vendors || vendorsData.vendors.length === 0) {
        throw new Error('No vendors found near your location.');
      }

      setVendors(vendorsData.vendors);
    } catch (err) {
      console.error('Error fetching vendors:', err);
      setError(err.message || 'Failed to load vendors. Please try again later.');
    } finally {
      setLoading(false);
    }
  };


  // Fetch vendors on component mount
  useEffect(() => {
    fetchVendors();
  }, [address]); // Re-run if address changes

  // Filter vendors or products by search query
  const filteredVendors = vendors.filter((vendor) =>
  vendor.dispensaryName.toLowerCase().includes(searchQuery.toLowerCase()) ||
  vendor.products?.some((product) =>
    product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    product.brand?.toLowerCase().includes(searchQuery.toLowerCase()) ||
    product.variation?.strain?.toLowerCase().includes(searchQuery.toLowerCase()) // Filter by strain
  )
);


  // Component Rendering
  if (!address) {
    return <p>No address set. Please go back and enter your address.</p>;
  }

  return (
    <div className="container-fluid mt-3">
      {/* Toolbar Section */}
      <div className="d-flex align-items-center justify-content-between bg-light p-3 rounded shadow-sm mb-3">
        {/* Address Section */}
        <div className="d-flex align-items-center">
          <GeoAltFill className="text-primary me-2" size={20} />
          <span className="me-2 text-dark fw-bold">{address}</span>
          <ChangeAddressLink />
        </div>

        {/* Search Bar */}
        <Form.Control
          type="text"
          placeholder="Search brands, products, vendors..."
          className="w-50"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>

      {/* Vendor Cards Section */}
      {loading ? (
        <p>Loading vendors...</p>
      ) : error ? (
        <p className="text-danger">{error}</p>
      ) : filteredVendors.length ? (
        <div className="vendor-list">
          {filteredVendors.map((vendor) => (
            <VendorCard key={vendor._id} vendor={vendor} />
          ))}
        </div>
      ) : (
        <p>No vendors or products match your search.</p>
      )}
    </div>
  );
};

export default NearbyVendorsPage;
