import React, { useState, useContext } from 'react';
import { CartContext } from '../context/CartContext';
import ProductDetails from './ProductDetails'; // Import the ProductDetails component

const ProductCard = ({ product, vendorName, logoUrl }) => {
  const { addToCart } = useContext(CartContext);
  const [showDetails, setShowDetails] = useState(false);

  const handleDetailsOpen = () => setShowDetails(true);
  const handleDetailsClose = () => setShowDetails(false);

  // Safeguard values
  const price = product.variation?.price ?? 0;
  const salePrice = product.variation?.salePrice ?? 0;

  return (
    <>
      <div
        className="card product-card shadow-sm"
        style={{ width: '14rem', position: 'relative', cursor: 'pointer', paddingTop: product.variation?.salePrice ? '2rem' : '0' }}
        onClick={handleDetailsOpen}
      >
        {/* SALE Banner */}
        {salePrice > 0 && (
          <div
            className="position-absolute top-0 start-0 w-100 text-center rounded-top"
            style={{
              backgroundColor: '#FF0080',
              color: 'white',
              fontSize: '0.8rem',
              padding: '0.3rem 0',
              fontWeight: 'bold',
            }}
          >
            SALE
          </div>
        )}

        {/* Card Body */}
        <div className="card-body d-flex flex-column justify-content-between">
          {/* Product Info Header */}
          <div className="d-flex justify-content-between">
            <span className="text-muted" style={{ fontSize: '0.8rem' }}>
              {product.category || 'Uncategorized'}
            </span>
            <span className="badge bg-light text-dark" style={{ fontSize: '0.75rem' }}>
              {product.variation?.amount || 'N/A'}
            </span>
          </div>

          {/* Product Image */}
          <div className="text-center my-1">
            <img
              src={product.variation?.image || '/placeholder-image.png'}
              alt={product.name || 'Product Image'}
              className="img-fluid"
              style={{ maxHeight: '120px', objectFit: 'contain' }}
            />
          </div>

          {/* Product Info */}
          <div className="text-start">
            <p className="fw-bold text-center mb-1" style={{ fontSize: '0.85rem' }}>
              {product.brand}
            </p>
            <p className="text-dark text-center mb-1" style={{ fontSize: '1rem', fontWeight: 'normal' }}>
              {product.name}
            </p>
            <p className="text-muted text-center mb-1" style={{ fontSize: '.8rem', fontWeight: 'normal' }}>
              {product.variation?.strain}
            </p>
          </div>

          {/* Footer Section */}
          <div className="d-flex justify-content-between align-items-center mt-2">
            {/* Price Section */}
            <div>
              {salePrice > 0 ? (
                <>
                  <span style={{ color: '#FF0080', fontWeight: 'bold' }}>
                    ${salePrice.toFixed(2)}
                  </span>
                  <span
                    style={{
                      textDecoration: 'line-through',
                      color: 'gray',
                      fontSize: '0.85rem',
                      marginLeft: '0.5rem',
                    }}
                  >
                    ${price.toFixed(2)}
                  </span>
                </>
              ) : (
                <span style={{ fontWeight: 'bold' }}>
                  ${price.toFixed(2)}
                </span>
              )}
            </div>
            {/* Add to Cart Button */}
            <button
              className="btn btn-primary rounded-circle"
              style={{
                width: '2.5rem',
                height: '2.5rem',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
              }}
              onClick={(e) => {
                e.stopPropagation(); // Prevent opening details modal when clicking "Add to Cart"
                addToCart({
                  ...product,
                  price: price,
                  salePrice: salePrice > 0 ? salePrice : null,
                  vendorName,
                  logoUrl,
                });
              }}
            >
              <i className="bi bi-plus-lg"></i>
            </button>
          </div>
        </div>
      </div>

      {/* Product Details Modal */}
      <ProductDetails show={showDetails} handleClose={handleDetailsClose} product={product} />
    </>
  );
};

export default ProductCard;
