import React from 'react';
import { Modal, Button } from 'react-bootstrap';

const [quantity, setQuantity] = useState(1);

const handleIncrement = () => setQuantity((prev) => prev + 1);
const handleDecrement = () => setQuantity((prev) => Math.max(1, prev - 1));

const handleAddToCart = () => {
  console.log('Added to cart:', { product, quantity });
};


const ProductDetails = ({ show, handleClose, product }) => {
  if (!product) return null;

  return (
    <Modal show={show} onHide={handleClose} centered>
      <Modal.Header closeButton>
        <Modal.Title><strong>{product.brand}</strong> | {product.category}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div className="text-center">
          {/* Product Image */}
          <img
            src={product.variation?.image || '/placeholder-image.png'}
            alt={product.name || 'Product Image'}
            className="img-fluid mb-4"
            style={{ maxHeight: '200px', objectFit: 'contain' }}
          />
        </div>
        {/* Product Info */}
        <div className="d-flex align-items-center mb-3">
  <h5 className="fw-bold mb-0 me-3">{product.name}</h5>
  {product.variation?.amount && (
    <span
      className="badge bg-light text-dark"
      style={{
        fontSize: '0.9rem',
        fontWeight: 'bold',
        borderRadius: '0.5rem',
        padding: '0.3rem 0.6rem',
      }}
    >
      {product.variation.amount}
    </span>
  )}
</div>
<p className="text-muted">{product.description || ''}</p>

<div className="mb-2 d-flex align-items-center">
  <strong>{product.variation?.strain || 'N/A'}</strong>
  {product.variation?.thcContent && (
    <span
      className="badge border border-secondary text-secondary ms-3"
      style={{
        fontSize: '0.9rem',
        fontWeight: 'bold',
        borderRadius: '0.5rem',
        padding: '0.3rem 0.6rem',
      }}
    >
      {product.variation.thcContent}% THC
    </span>
  )}
</div>



      </Modal.Body>
      <Modal.Footer className="d-flex justify-content-between align-items-center">
  {/* Left: Price Badge */}
  <div>
    {product.variation?.salePrice ? (
      <>
        {/* Sale Price Badge */}
        <span
          className="badge bg-light"
          style={{
            fontSize: '1.2rem',
            fontWeight: 'bold',
            color: '#FF0080',
            borderRadius: '0.5rem',
            padding: '0.6rem 1.2rem',
          }}
        >
          ${product.variation.salePrice.toFixed(2)}
        </span>
        {/* Crossed-Out Original Price */}
        <span
          style={{
            textDecoration: 'line-through',
            color: 'gray',
            fontSize: '1rem',
            marginLeft: '0.5rem',
          }}
        >
          ${product.variation.price.toFixed(2)}
        </span>
      </>
    ) : (
      <span
        className="badge bg-light text-dark"
        style={{
          fontSize: '1.2rem',
          fontWeight: 'bold',
          borderRadius: '0.5rem',
          padding: '0.6rem 1.2rem',
        }}
      >
        ${product.variation?.price?.toFixed(2) || 'N/A'}
      </span>
    )}
  </div>

  {/* Middle: Quantity Controls */}
  <div className="d-flex align-items-center">
    <Button
      variant="outline-secondary"
      size="sm"
      style={{ fontSize: '1.2rem', padding: '0.3rem 0.6rem' }}
      onClick={() => handleDecrement()}
    >
      −
    </Button>
    <span
      style={{
        fontSize: '1.2rem',
        fontWeight: 'bold',
        margin: '0 1rem',
        minWidth: '2rem',
        textAlign: 'center',
      }}
    >
      {quantity || 1}
    </span>
    <Button
      variant="outline-secondary"
      size="sm"
      style={{ fontSize: '1.2rem', padding: '0.3rem 0.6rem' }}
      onClick={() => handleIncrement()}
    >
      +
    </Button>
  </div>

  {/* Right: Add to Cart Button */}
  <div>
    <Button
      variant="primary"
      className="rounded-circle d-flex align-items-center justify-content-center"
      style={{
        width: '3rem',
        height: '3rem',
        fontSize: '1.5rem',
        fontWeight: 'bold',
      }}
      onClick={() => handleAddToCart()}
    >
      +
    </Button>
  </div>
</Modal.Footer>

    </Modal>
  );
};

export default ProductDetails;
