import React, { useState, useEffect, useRef } from "react";
import { Form, Button, Alert, Row, Col, Card } from "react-bootstrap";
import axios from "axios";
import { useJsApiLoader, GoogleMap, Marker } from "@react-google-maps/api";
import { Link } from "react-router-dom";
import "./VendorRegistration.css";

const VendorRegistration = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    contactNumber: "",
    email: "",
    password: "",
    dispensaryName: "",
    dispensaryType: "",
    storefrontAddress: {
      formatted: "",
      coordinates: [null, null],
      type: "Point",
    },
    minOrder: "",
    acceptedPayments: [{ method: "Cash", fee: 0 }],
    deliveryZone: null,
  });
  const [formErrors, setFormErrors] = useState({});
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(null);
  const [showMap, setShowMap] = useState(false);
  const inputRef = useRef(null);
  const [animateCard, setAnimateCard] = useState(false);

  const { isLoaded, loadError } = useJsApiLoader({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries: ["places"],
  });

  useEffect(() => {
    const timer = setTimeout(() => {
      setAnimateCard(true);
    }, 100);
    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    if (!isLoaded || !inputRef.current) return;

    const autocomplete = new window.google.maps.places.Autocomplete(inputRef.current, {
      types: ["geocode"],
    });

    autocomplete.addListener("place_changed", () => {
      const place = autocomplete.getPlace();
      if (place?.formatted_address && place?.geometry?.location) {
        const formattedAddress = place.formatted_address;
        const coordinates = [
          place.geometry.location.lng(),
          place.geometry.location.lat(),
        ];

        setFormData((prev) => ({
          ...prev,
          storefrontAddress: {
            type: "Point",
            formatted: formattedAddress,
            coordinates: coordinates.map(Number),
          },
        }));
      }
    });
  }, [isLoaded, inputRef.current]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handlePhoneChange = (e) => {
    const rawValue = e.target.value.replace(/\D/g, "");

    // Create formatted version for display
    const formatted = rawValue.replace(
      /^(\d{0,3})(\d{0,3})(\d{0,4})$/,
      (match, areaCode, prefix, lineNumber) => {
        if (lineNumber) return `(${areaCode}) ${prefix}-${lineNumber}`;
        if (prefix) return `(${areaCode}) ${prefix}`;
        if (areaCode) return `(${areaCode}`;
        return "";
      }
    );

    // Create E.164 version for functionality
    const e164 = rawValue.length === 10 ? `+1${rawValue}` : '';

    setFormData((prev) => ({ 
        ...prev, 
        contactNumber: {
            formatted,
            e164
        }
    }));
};

  const validateStepOne = () => {
    const errors = {};
    if (!formData.firstName.trim()) errors.firstName = "First name is required";
    if (!formData.lastName.trim()) errors.lastName = "Last name is required";
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email))
      errors.email = "Invalid email address";
      if (!formData.contactNumber?.e164 || !formData.contactNumber.e164.replace(/^\+1/, '').length === 10) {
        errors.contactNumber = "Phone number must be 10 digits";
      }
    if (formData.password.length < 6) errors.password = "Password must be at least 6 characters";
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleNext = (e) => {
    e.preventDefault();
    if (validateStepOne()) {
      setCurrentStep(2);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formattedFormData = {
        ...formData,
        minOrder: parseFloat(formData.minOrder) || 0,
      };

      // Validate storefront address for Pickup and Pickup & Delivery
      if (["Pickup", "Pickup & Delivery"].includes(formData.dispensaryType)) {
        if (
          !formData.storefrontAddress.coordinates ||
          formData.storefrontAddress.coordinates.length !== 2 ||
          isNaN(formData.storefrontAddress.coordinates[0]) ||
          isNaN(formData.storefrontAddress.coordinates[1]) ||
          !formData.storefrontAddress.formatted
        ) {
          setError("Storefront address is incomplete or invalid. Please select a valid location from the dropdown.");
          return;
        }

        formattedFormData.storefrontAddress = {
          type: "Point",
          formatted: formData.storefrontAddress.formatted,
          coordinates: formData.storefrontAddress.coordinates.map(Number),
        };
      }

      // Validate delivery zone for Delivery and Pickup & Delivery
      if (["Delivery", "Pickup & Delivery"].includes(formData.dispensaryType)) {
        if (
          !formData.deliveryZone ||
          !formData.deliveryZone.coordinates ||
          formData.deliveryZone.coordinates.length !== 2
        ) {
          setError("Please select a delivery zone on the map.");
          return;
        }

        formattedFormData.deliveryZone = {
          type: "Point",
          coordinates: formData.deliveryZone.coordinates.map(Number),
        };
      }

      console.log("Submitting data to backend:", formattedFormData);

      const response = await axios.post("/api/auth/register", formattedFormData);
      if (response.data.success) setSuccess(true);
    } catch (err) {
      setError(err.response?.data?.message || "Error registering vendor.");
    }
  };

  if (loadError) return <Alert variant="danger">Error loading Google Maps API</Alert>;
  if (!isLoaded) return <p>Loading...</p>;

  return (
    <div className="container py-5 mb-4">
      <div className="d-flex justify-content-center">
        <Card
          className={`w-100 shadow-sm animated-card ${animateCard ? "slide-in" : ""}`}
          style={{ maxWidth: "90%" }}
        >
          <Card.Body>
            {success ? (
              <div className="text-center">
                <Alert variant="success" className="mb-4">
                  Registration successful! Your account has been created.
                </Alert>
                <Link to="/login" className="btn btn-primary">
                  Go to Login
                </Link>
              </div>
            ) : (
              <>
                {error && <Alert variant="danger">{error}</Alert>}

                {currentStep === 1 && (
                  <Form onSubmit={handleNext}>
                    <h3 className="mt-4 mb-4 text-center font-semibold">
                      Create your free account
                    </h3>
                    <p className="text-center text-muted mb-5" style={{ fontSize: "15px" }}>
                      Quickly add products, set your store settings, and start receiving orders.
                    </p>
                    <Row>
                      <Col md={6}>
                        <Form.Group className="mb-3">
                          <Form.Label>First Name</Form.Label>
                          <Form.Control
                            type="text"
                            name="firstName"
                            value={formData.firstName}
                            onChange={handleChange}
                            isInvalid={!!formErrors.firstName}
                          />
                          <Form.Control.Feedback type="invalid">
                            {formErrors.firstName}
                          </Form.Control.Feedback>
                        </Form.Group>
                      </Col>
                      <Col md={6}>
                        <Form.Group className="mb-3">
                          <Form.Label>Last Name</Form.Label>
                          <Form.Control
                            type="text"
                            name="lastName"
                            value={formData.lastName}
                            onChange={handleChange}
                            isInvalid={!!formErrors.lastName}
                          />
                          <Form.Control.Feedback type="invalid">
                            {formErrors.lastName}
                          </Form.Control.Feedback>
                        </Form.Group>
                      </Col>
                    </Row>
                    <Form.Group className="mb-3">
                      <Form.Label>Phone Number</Form.Label>
                      <Form.Control
                        type="text"
                        name="contactNumber"
                        value={formData.contactNumber}
                        onChange={handlePhoneChange}
                        isInvalid={!!formErrors.contactNumber}
                      />
                      <Form.Control.Feedback type="invalid">
                        {formErrors.contactNumber}
                      </Form.Control.Feedback>
                    </Form.Group>
                    <Form.Group className="mb-3">
                      <Form.Label>Email</Form.Label>
                      <Form.Control
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        isInvalid={!!formErrors.email}
                      />
                      <Form.Control.Feedback type="invalid">
                        {formErrors.email}
                      </Form.Control.Feedback>
                    </Form.Group>
                    <Form.Group className="mb-3">
                      <Form.Label>Password</Form.Label>
                      <Form.Control
                        type="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                        isInvalid={!!formErrors.password}
                      />
                      <Form.Control.Feedback type="invalid">
                        {formErrors.password}
                      </Form.Control.Feedback>
                    </Form.Group>

                    <Button
                      type="submit"
                      variant="primary"
                      className="ms-auto d-flex justify-content-center align-items-center rounded mt-4"
                      style={{ width: "80px", height: "50px" }}
                    >
                      <i className="bi bi-arrow-right"></i>
                    </Button>
                  </Form>
                )}

                {currentStep === 2 && (
                  <Form onSubmit={handleSubmit}>
                    <Form.Group className="mb-3">
                      <Form.Label>Dispensary Name</Form.Label>
                      <Form.Control
                        type="text"
                        name="dispensaryName"
                        value={formData.dispensaryName}
                        onChange={handleChange}
                      />
                    </Form.Group>
                    <Form.Group className="mb-3">
                      <Form.Label>Dispensary Type</Form.Label>
                      <div>
                        {["Delivery", "Pickup", "Pickup & Delivery"].map((type) => (
                          <Form.Check
                            key={type}
                            type="radio"
                            name="dispensaryType"
                            label={type}
                            value={type}
                            onChange={(e) => {
                              handleChange(e);
                              setShowMap(["Delivery", "Pickup & Delivery"].includes(e.target.value));
                            }}
                          />
                        ))}
                      </div>
                    </Form.Group>

                    {/* Storefront Address Input - Show for Pickup and Pickup & Delivery */}
                    {["Pickup", "Pickup & Delivery"].includes(formData.dispensaryType) && (
                      <Form.Group className="mb-3">
                        <Form.Label>Storefront Address</Form.Label>
                        <Form.Control
                          type="text"
                          ref={inputRef}
                          name="storefrontAddress"
                          value={formData.storefrontAddress.formatted}
                          placeholder="Start typing to search for your address"
                          onChange={(e) =>
                            setFormData((prev) => ({
                              ...prev,
                              storefrontAddress: {
                                ...prev.storefrontAddress,
                                formatted: e.target.value,
                              },
                            }))
                          }
                        />
                      </Form.Group>
                    )}

                    {/* Delivery Zone Map - Show for Delivery and Pickup & Delivery */}
                    {["Delivery", "Pickup & Delivery"].includes(formData.dispensaryType) && (
                      <>
                        <h5>Select Delivery Zone</h5>
                        <p>Orders will be limited to within 20 miles of marker.</p>
                        <div
                          className="map-container mb-5 card rounded"
                          style={{ height: "300px", width: "100%" }}
                        >
                          <GoogleMap
                            center={{ lat: 34.0522, lng: -118.2437 }}
                            zoom={10}
                            mapContainerStyle={{ height: "300px", width: "100%" }}
                            onClick={(e) => {
                              const lat = e.latLng.lat();
                              const lng = e.latLng.lng();
                              setFormData((prev) => ({
                                ...prev,
                                deliveryZone: {
                                  type: "Point",
                                  coordinates: [lng, lat],
                                },
                              }));
                            }}
                          >
                            {formData.deliveryZone && (
                              <Marker
                                position={{
                                  lat: formData.deliveryZone.coordinates[1],
                                  lng: formData.deliveryZone.coordinates[0],
                                }}
                              />
                            )}
                          </GoogleMap>
                        </div>
                      </>
                    )}

                    <div className="d-flex justify-content-center mb-2">
                      <Button
                        variant="link"
                        className="text-secondary text-decoration-none"
                        onClick={() => setCurrentStep(1)}
                      >
                        ← Back
                      </Button>
                    </div>
                    <Button type="submit" variant="primary" className="w-100" style={{ height: "50px" }}>
                      Register
                    </Button>
                  </Form>
                )}
              </>
            )}
          </Card.Body>
        </Card>
      </div>
    </div>
  );
};

export default VendorRegistration;
