import React from 'react';

const categories = ['Flower', 'Pre-roll', 'Vape', 'Edible', 'Extract', 'Misc'];

const CategoryFilter = ({ selectedCategory, onCategoryChange }) => {
  return (
    <div
      className="category-filter mb-3"
      style={{
        backgroundColor: '#f8f9fa', // Section background color
        overflowX: 'auto', // Enable horizontal scrolling for overflow
        whiteSpace: 'nowrap', // Prevent wrapping
        padding: '0.5rem 1rem', // Add padding for better spacing
        borderRadius: '8px', // Rounded corners for the section
        display: 'flex',
        gap: '1rem', // Add spacing between category texts
      }}
    >
      {categories.map((category) => (
        <span
          key={category}
          onClick={() => onCategoryChange(category)}
          style={{
            cursor: 'pointer', // Pointer cursor for clickable text
            fontWeight: selectedCategory === category ? 'bold' : 'normal', // Bold text for selected
            color: selectedCategory === category ? '#333' : '#555', // Darker color for selected
            textTransform: 'uppercase', // Uppercase for all text
            borderBottom: selectedCategory === category ? '2px solid #333' : 'none', // Underline selected
            paddingBottom: '0.25rem', // Padding for underline spacing
            transition: 'color 0.3s ease, border-bottom 0.3s ease', // Smooth transitions
          }}
          className={`category-item ${
            selectedCategory === category ? 'active' : ''
          }`}
        >
          {category}
        </span>
      ))}
    </div>
  );
};

export default CategoryFilter;
