import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import ProductCard from './ProductCard';
import axios from 'axios';

const VendorCard = ({ vendor }) => {
  const navigate = useNavigate();
  const [vendorProducts, setVendorProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const handleViewMenu = () => {
    navigate(`/vendor/${vendor._id}`);
  };

  useEffect(() => {
    const fetchVendorProducts = async () => {
      try {
        // Fetch products for the specific vendor from the VendorProduct collection
        const response = await axios.get(`/api/vendor/inventory/${vendor._id}`);
        setVendorProducts(response.data); // Save fetched products
        setLoading(false);
      } catch (err) {
        console.error('Error fetching vendor products:', err);
        setError('Failed to load products');
        setLoading(false);
      }
    };

    fetchVendorProducts();
  }, [vendor._id]);

  return (
    <div className="vendor-card shadow p-3 mb-4 rounded" style={{ border: '1px solid #ddd' }}>
      {/* Vendor Header */}
      <div className="d-flex align-items-center justify-content-between">
        <div className="d-flex align-items-center">
          <img
            src={vendor.logoUrl || '/placeholder-logo.png'}
            alt={vendor.dispensaryName}
            className="rounded"
            style={{
              width: '44px',
              height: '44px',
              objectFit: 'cover',
              marginRight: '10px',
            }}
          />
          <div>
            <h6 className="mb-1" style={{ fontSize: '1rem', fontWeight: 'bold' }}>
              {vendor.dispensaryName}
            </h6>
            <p className="text-muted mb-0" style={{ fontSize: '0.85rem' }}>
              Minimum Order: ${vendor.minOrder || 'N/A'} | {vendor.milesAway || 'N/A'} miles away
            </p>
          </div>
        </div>
        <button className="btn btn-primary btn-sm" onClick={handleViewMenu}>
          Menu
        </button>
      </div>

      {/* Products Row */}
      <div className="products-preview mt-3">
        {loading ? (
          <p>Loading products...</p>
        ) : error ? (
          <p className="text-danger">{error}</p>
        ) : vendorProducts.length ? (
          <div className="d-flex overflow-auto" style={{ paddingBottom: '10px', scrollbarWidth: 'thin' }}>
            {vendorProducts.slice(0, 10).map((product) => (
              <div className="me-2" style={{ minWidth: '200px', flex: '0 0 auto' }}>
                <ProductCard
                key={product._id}
                  product={product}
                  vendorName={vendor.dispensaryName}
                  logoUrl={vendor.logoUrl}
                />
              </div>
            ))}
          </div>
        ) : (
          <p className="text-muted">No products available</p>
        )}
      </div>
    </div>
  );
};

export default VendorCard;
