import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext'; // Import the AuthContext hook

const AddProductPage = () => {
  const { auth } = useAuth(); // Access vendor details from the context
  const [brand, setBrand] = useState('');
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [customDetails, setCustomDetails] = useState({ price: '', stock: '', weight: '' });
  const [showDropdown, setShowDropdown] = useState(false);

  // Fetch products by brand
  useEffect(() => {
    if (brand) {
      fetch(`/api/products/brand/${brand}`)
        .then((response) => response.json())
        .then((data) => setProducts(data))
        .catch((error) => console.error('Error fetching products:', error));
    } else {
      setProducts([]);
    }
  }, [brand]);

  const handleAddToInventory = async () => {
    if (!selectedProduct || !customDetails.price || !customDetails.stock || !customDetails.weight) {
      alert('Please fill out all fields');
      return;
    }

    try {
      // Fetch the current inventory
      const inventoryResponse = await fetch(`/api/vendor/inventory/${auth.vendorId}`);
      const inventory = await inventoryResponse.json();

      // Check if the product is already in inventory
      const productExists = inventory.some(
        (item) => item.productId._id === selectedProduct._id
      );

      if (productExists) {
        alert('This product is already in your inventory.');
        return; // Stop further execution
      }

      // Proceed to add the product to inventory
      const response = await fetch('/api/vendor/inventory', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          vendorId: auth.vendorId,
          productId: selectedProduct._id,
          price: customDetails.price,
          stock: customDetails.stock,
          weight: customDetails.weight,
        }),
      });

      const data = await response.json();

      if (response.ok) {
        // Success case
        alert(data.message);
        setBrand('');
        setSelectedProduct(null);
        setCustomDetails({ price: '', stock: '', weight: '' });
        setShowDropdown(false);
      } else if (response.status === 400 && data.message === 'This product is already in your inventory.') {
        // Handle duplicate product
        alert(data.message);
      } else {
        // Handle generic errors
        alert(data.error || 'Failed to add product to inventory.');
      }
    } catch (error) {
      console.error('Error adding product to inventory:', error);
      alert('An error occurred while adding the product.');
    }
  };




  const handleProductClick = (product) => {
    setSelectedProduct(product);
    setShowDropdown(false); // Hide dropdown on selection
  };

  return (
    <div>
      <h1>Add Products to Your Menu</h1>

      {/* Brand Input */}
      <div style={{ position: 'relative' }}>
        <label>Enter Brand:</label>
        <input
          type="text"
          value={brand}
          onChange={(e) => {
            setBrand(e.target.value);
            setShowDropdown(true);
          }}
          placeholder="e.g., Brand A"
        />
        {/* Dropdown Menu */}
        {showDropdown && products.length > 0 && (
          <div
            style={{
              position: 'absolute',
              top: '100%',
              left: 0,
              right: 0,
              maxHeight: '200px',
              overflowY: 'auto',
              border: '1px solid #ccc',
              backgroundColor: '#fff',
              zIndex: 1000,
            }}
          >
            {products.map((product) => (
              <div
                key={product._id}
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  padding: '8px',
                  cursor: 'pointer',
                  borderBottom: '1px solid #eee',
                }}
                onClick={() => handleProductClick(product)}
              >
                <img
                  src={product.imageUrl}
                  alt={product.name}
                  style={{ width: '50px', height: '50px', marginRight: '10px' }}
                />
                <div>
                  <div style={{ fontWeight: 'bold' }}>{product.brand}</div>
                  <div>{product.name}</div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Customization Form */}
      {selectedProduct && (
        <div>
          <h2>Customize {selectedProduct.name}</h2>
          <input
            type="number"
            placeholder="Price"
            value={customDetails.price}
            onChange={(e) => setCustomDetails({ ...customDetails, price: e.target.value })}
          />
          <input
            type="number"
            placeholder="Stock"
            value={customDetails.stock}
            onChange={(e) => setCustomDetails({ ...customDetails, stock: e.target.value })}
          />
          <input
            type="text"
            placeholder="Weight (e.g., 1g, 3.5g)"
            value={customDetails.weight}
            onChange={(e) => setCustomDetails({ ...customDetails, weight: e.target.value })}
          />
          <button onClick={handleAddToInventory}>Add to Inventory</button>
        </div>
      )}
    </div>
  );
};

export default AddProductPage;
