import React, { createContext, useState, useEffect, useCallback } from 'react';
import axios from 'axios';

export const VendorContext = createContext();

export const VendorProvider = ({ vendorId, children }) => {
  const [vendor, setVendor] = useState(null);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [vendorStatus, setVendorStatus] = useState(null);

  // Calculate vendor status based on business hours
  const calculateVendorStatus = useCallback((businessHours) => {
    if (!businessHours) return null;

    const now = new Date();
    const days = ['sunday', 'monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday'];
    const currentDay = days[now.getDay()];

    // Format current time as HH:mm for comparison
    const currentTime = now.toLocaleTimeString('en-US', { 
      hour12: false, 
      hour: '2-digit', 
      minute: '2-digit'
    });

    const todayHours = businessHours[currentDay];
    if (!todayHours?.open || !todayHours?.close) return null;

    const isOpen = currentTime >= todayHours.open && currentTime <= todayHours.close;

    if (isOpen) {
      return {
        status: 'open',
        until: todayHours.close.split(':')[0],
        currentDay
      };
    }

    // If closed, find next opening time
    if (currentTime < todayHours.open) {
      // Will open later today
      return {
        status: 'closed',
        until: todayHours.open.split(':')[0],
        currentDay
      };
    }

    // Find next day's opening time
    let nextDay = currentDay;
    let daysChecked = 0;

    while (daysChecked < 7) {
      const currentIndex = days.indexOf(nextDay);
      nextDay = days[(currentIndex + 1) % 7];
      const nextDayHours = businessHours[nextDay];

      if (nextDayHours?.open) {
        return {
          status: 'closed',
          until: nextDayHours.open.split(':')[0],
          currentDay: nextDay
        };
      }
      daysChecked++;
    }

    return null;
  }, []);

  // Update status every minute
  useEffect(() => {
    if (vendor?.businessHours) {
      const updateStatus = () => {
        const newStatus = calculateVendorStatus(vendor.businessHours);
        setVendorStatus(newStatus);
      };

      // Initial calculation
      updateStatus();

      // Set up interval for updates
      const intervalId = setInterval(updateStatus, 60000); // Update every minute

      return () => clearInterval(intervalId);
    }
  }, [vendor?.businessHours, calculateVendorStatus]);

  const normalizeProduct = useCallback((vp, vendorId) => ({
    _id: vp._id,
    productId: vp.productId?._id,
    name: vp.name,
    brand: vp.brand,
    category: vp.category,
    strain: vp.variation?.strain || "N/A",
    thcContent: vp.variation?.thcContent || null,
    price: vp.variation?.price || 0,
    salePrice: vp.variation?.salePrice || null,
    image: vp.variation?.image || vp.productId?.images?.[0] || "/placeholder-image.png",
    description: vp.description || vp.productId?.description || "",
    amount: vp.variation?.amount || "N/A",
    vendorId,
    status: vp.status
  }), []);

  // Fetch vendor and products data
  const fetchVendorAndProducts = useCallback(async (id) => {
    setLoading(true);
    setError(null);

    try {
      const [vendorResponse, productsResponse] = await Promise.all([
        axios.get(`http://localhost:5001/api/vendors/${id}`, { withCredentials: true }),
        axios.get(`http://localhost:5001/api/vendor/inventory/public/${id}`)
      ]);

      const vendorData = vendorResponse.data.data || {};
      const productsData = productsResponse.data.data || [];

      const formattedProducts = productsData.map(vp => normalizeProduct(vp, id));

      setVendor({
        ...vendorData,
        products: formattedProducts
      });

      setProducts(formattedProducts);

      // Calculate initial status
      const initialStatus = calculateVendorStatus(vendorData.businessHours);
      setVendorStatus(initialStatus);

    } catch (err) {
      console.error("Failed to fetch vendor data:", err.message);
      setError("Failed to load vendor or products data.");
    } finally {
      setLoading(false);
    }
  }, [normalizeProduct, calculateVendorStatus]);

  // Update minimum order
  const updateMinOrder = async (formData) => {
    try {
      const response = await axios.put(
        'http://localhost:5001/api/vendors/min-order',
        formData,
        { withCredentials: true }
      );

      if (response.data.success) {
        setVendor(prev => ({ 
          ...prev, 
          minOrder: formData.minOrder,
          acceptedPayments: formData.acceptedPayments 
        }));
        return { success: true, message: 'Settings updated successfully!' };
      }
      return { success: false, message: response.data.message || 'Failed to update settings.' };
    } catch (err) {
      console.error('Error updating settings:', err.message);
      return { success: false, message: 'An error occurred while updating settings.' };
    }
  };

  // Update business hours
  const updateBusinessHours = async (businessHours) => {
    try {
      const response = await axios.put(
        'http://localhost:5001/api/vendors/business-hours',
        { businessHours },
        { withCredentials: true }
      );

      if (response.data.success) {
        setVendor(prev => ({ ...prev, businessHours }));
        // Recalculate status with new hours
        const newStatus = calculateVendorStatus(businessHours);
        setVendorStatus(newStatus);
        return { success: true, message: 'Business hours updated successfully!' };
      }
      return { success: false, message: response.data.message || 'Failed to update business hours.' };
    } catch (err) {
      console.error('Error updating business hours:', err.message);
      return { success: false, message: 'An error occurred while updating business hours.' };
    }
  };

  // Refresh vendor data
  const refreshVendorData = async () => {
    if (vendorId) {
      await fetchVendorAndProducts(vendorId);
    }
  };

  useEffect(() => {
    if (vendorId) {
      fetchVendorAndProducts(vendorId);
    }
  }, [vendorId, fetchVendorAndProducts]);

  return (
    <VendorContext.Provider
      value={{
        vendor,
        products,
        loading,
        error,
        vendorStatus,
        updateMinOrder,
        updateBusinessHours,
        refreshVendorData,
      }}
    >
      {children}
    </VendorContext.Provider>
  );
};
