import React, { useState } from "react";
import { Form, Button, Alert } from "react-bootstrap";
import axios from "axios";

const VendorRegistration = () => {
  const [step, setStep] = useState(1); // Step tracker
  const [formData, setFormData] = useState({
    licenseState: "",
    licenseNumber: "",
    dispensaryType: "",
    dispensaryName: "",
    businessAddress: {
      street: "",
      city: "",
      state: "",
      zip: "",
    },
    firstName: "",
    lastName: "",
    contactNumber: "",
    email: "",
    password: "",
    agreeToTerms: false,
  });
  const [showValidation, setShowValidation] = useState(false); // Validation toggle
  const [error, setError] = useState(null); // Backend error handling
  const [success, setSuccess] = useState(false); // Registration success message

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleNestedChange = (section, field, value) => {
    setFormData((prev) => ({
      ...prev,
      [section]: { ...prev[section], [field]: value },
    }));
  };

  const handleNextStep = () => setStep((prev) => prev + 1);
  const handlePrevStep = () => setStep((prev) => prev - 1);

  const handleSubmit = async () => {
    try {
      setError(null); // Reset error state
      const response = await axios.post("/api/auth/register", formData);

      if (response.data.success) {
        setSuccess(true);
      }
    } catch (err) {
      setError(err.response?.data?.message || "Error registering vendor. Please try again.");
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4 text-center">Vendor Registration</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">Vendor registered successfully!</Alert>}

      {step === 1 && (
        <>
          <h5>Step 1: Dispensary Details</h5>
          <Form.Group className="mb-3">
            <Form.Label>License Info</Form.Label>
            <div className="d-flex">
              <Form.Select
                name="licenseState"
                value={formData.licenseState}
                onChange={handleChange}
                required
                style={{ width: "120px" }}
              >
                <option value="">State</option>
                <option value="AZ">AZ</option>
                <option value="CA">CA</option>
                <option value="CO">CO</option>
                <option value="MI">MI</option>
                <option value="NJ">NJ</option>
                <option value="NY">NY</option>
                <option value="OR">OR</option>
              </Form.Select>
              <Form.Control
                type="text"
                placeholder="License Number"
                name="licenseNumber"
                value={formData.licenseNumber}
                onChange={handleChange}
                required
                className="ms-3"
              />
            </div>
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Dispensary Type</Form.Label>
            <Form.Select
              name="dispensaryType"
              value={formData.dispensaryType}
              onChange={handleChange}
              required
            >
              <option value="">Select a Service</option>
              <option value="PICKUP">PICKUP</option>
              <option value="DELIVERY">DELIVERY</option>
              <option value="PICKUP & DELIVERY">PICKUP & DELIVERY</option>
            </Form.Select>
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Dispensary Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter your dispensary name"
              name="dispensaryName"
              value={formData.dispensaryName}
              onChange={handleChange}
              required
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Business Address</Form.Label>
            <Form.Control
              type="text"
              placeholder="Street"
              value={formData.businessAddress.street}
              onChange={(e) => handleNestedChange("businessAddress", "street", e.target.value)}
            />
            <Form.Control
              type="text"
              placeholder="City"
              className="mt-2"
              value={formData.businessAddress.city}
              onChange={(e) => handleNestedChange("businessAddress", "city", e.target.value)}
            />
            <Form.Control
              type="text"
              placeholder="State"
              className="mt-2"
              value={formData.businessAddress.state}
              onChange={(e) => handleNestedChange("businessAddress", "state", e.target.value)}
            />
            <Form.Control
              type="text"
              placeholder="Zip Code"
              className="mt-2"
              value={formData.businessAddress.zip}
              onChange={(e) => handleNestedChange("businessAddress", "zip", e.target.value)}
            />
          </Form.Group>
        </>
      )}
      {step === 2 && (
        <>
          <h5>Step 2: Business Owner Details</h5>
          <div className="d-flex">
            <Form.Group className="me-2">
              <Form.Label>First Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter your first name"
                name="firstName"
                value={formData.firstName}
                onChange={handleChange}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Last Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter your last name"
                name="lastName"
                value={formData.lastName}
                onChange={handleChange}
              />
            </Form.Group>
          </div>
          <Form.Group className="mb-3">
            <Form.Label>Phone Number</Form.Label>
            <Form.Control
              type="tel"
              placeholder="Enter your phone number"
              name="contactNumber"
              value={formData.contactNumber}
              onChange={handleChange}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              placeholder="Enter your email"
              name="email"
              value={formData.email}
              onChange={handleChange}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Create a password"
              name="password"
              value={formData.password}
              onChange={handleChange}
            />
          </Form.Group>
        </>
      )}
      {step === 3 && (
        <>
          <h5>Step 3: Terms & Conditions</h5>
          <Form.Group>
            <Form.Check
              type="checkbox"
              label="Agree to terms"
              checked={formData.agreeToTerms}
              name="agreeToTerms"
              onChange={handleChange}
            />
          </Form.Group>
        </>
      )}
      <div>
        {step > 1 && <Button onClick={handlePrevStep}>Back</Button>}
        {step < 3 && <Button onClick={handleNextStep}>Next</Button>}
        {step === 3 && <Button onClick={handleSubmit}>Submit</Button>}
      </div>
    </div>
  );
};

export default VendorRegistration;


const handleSubmit = async () => {
  try {
    setError(null); // Reset error state
    const response = await axios.post("/api/auth/register", formData);

    if (response.data.success) {
      setSuccess(true);
    }
  } catch (err) {
    setError(err.response?.data?.message || "Error registering vendor. Please try again.");
  }
};
