import React, { useState, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker, InfoWindow } from '@react-google-maps/api';

const VendorMap = () => {
  const [vendors, setVendors] = useState([]);
  const [selectedVendor, setSelectedVendor] = useState(null);
  const [mapCenter, setMapCenter] = useState({
    lat: 34.0522,
    lng: -118.2437
  });
  const [userLocation, setUserLocation] = useState(null);

  const mapStyles = {
    height: "100vh",
    width: "100%"
  };

  useEffect(() => {
    // Get user's location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
          setMapCenter({
            lat: position.coords.latitude,
            lng: position.coords.longitude
          });
        },
        (error) => {
          console.error('Error getting location:', error);
        }
      );
    }

    // Fetch vendors
    const fetchVendors = async () => {
      try {
        const response = await fetch('/api/vendors/nearby');
        const data = await response.json();

        if (data.success && data.vendors) {
          // Transform vendor data to ensure coordinates are numbers
          const formattedVendors = data.vendors.map(vendor => ({
            ...vendor,
            coordinates: vendor.storefrontAddress?.coordinates ? {
              lat: Number(vendor.storefrontAddress.coordinates[1]),
              lng: Number(vendor.storefrontAddress.coordinates[0])
            } : null
          })).filter(vendor => vendor.coordinates);

          setVendors(formattedVendors);
        }
      } catch (error) {
        console.error('Error fetching vendors:', error);
      }
    };

    fetchVendors();
  }, []);

  const VendorCard = ({ vendor, isSelected, onClick }) => (
    <div 
      className={`p-4 bg-white rounded-lg shadow-md cursor-pointer transition-all ${
        isSelected ? 'ring-2 ring-blue-500' : ''
      }`}
      onClick={onClick}
    >
      <div className="flex items-center">
        <img 
          src={vendor.logoUrl} 
          alt={vendor.dispensaryName} 
          className="w-16 h-16 object-cover rounded-full"
        />
        <div className="ml-4">
          <h3 className="font-semibold text-lg">{vendor.dispensaryName}</h3>
          <p className="text-sm text-gray-600">{vendor.storefrontAddress.formatted}</p>
          <p className="text-sm">{vendor.dispensaryType}</p>
          {vendor.minOrder > 0 && (
            <p className="text-sm">Min. Order: ${vendor.minOrder}</p>
          )}
        </div>
      </div>
    </div>
  );

  return (
    <div className="flex h-screen">
      {/* Map Section */}
      <div className="w-1/2 h-full relative">
        <LoadScript googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}>
          <GoogleMap
            mapContainerStyle={mapStyles}
            zoom={12}
            center={mapCenter}
            options={{
              zoomControl: true,
              streetViewControl: false,
              mapTypeControl: false,
              fullscreenControl: false,
            }}
          >
            {userLocation && (
              <Marker
                position={userLocation}
                icon={{
                  url: "data:image/svg+xml,%3Csvg width='24' height='24' xmlns='http://www.w3.org/2000/svg'%3E%3Ccircle cx='12' cy='12' r='7' fill='%234285F4' stroke='white' stroke-width='2'/%3E%3C/svg%3E",
                  scaledSize: { width: 24, height: 24 }
                }}
              />
            )}

            {vendors.map(vendor => (
              vendor.coordinates && (
                <Marker
                  key={vendor._id}
                  position={vendor.coordinates}
                  onClick={() => setSelectedVendor(vendor)}
                  icon={{
                    url: vendor.logoUrl,
                    scaledSize: { width: 40, height: 40 }
                  }}
                />
              )
            ))}

            {selectedVendor && (
              <InfoWindow
                position={selectedVendor.coordinates}
                onCloseClick={() => setSelectedVendor(null)}
              >
                <div className="p-2">
                  <h3 className="font-semibold text-lg">{selectedVendor.dispensaryName}</h3>
                  <p className="text-sm text-gray-600">{selectedVendor.storefrontAddress.formatted}</p>
                  <p className="text-sm">{selectedVendor.dispensaryType}</p>
                </div>
              </InfoWindow>
            )}
          </GoogleMap>
        </LoadScript>
      </div>

      {/* Vendor Cards Section */}
      <div className="w-1/2 h-full overflow-y-auto bg-gray-50 p-4">
        <h2 className="text-2xl font-semibold mb-4">Nearby Dispensaries</h2>
        <div className="space-y-4">
          {vendors.map(vendor => (
            <VendorCard
              key={vendor._id}
              vendor={vendor}
              isSelected={selectedVendor?._id === vendor._id}
              onClick={() => {
                setSelectedVendor(vendor);
                setMapCenter(vendor.coordinates);
              }}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default VendorMap;
