import React, { useState } from "react";
import { Form, Button, Alert } from "react-bootstrap";
import axios from "axios";

const VendorRegistration = () => {
  // All the state and methods remain the same...
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    licenseState: "",
    licenseNumber: "",
    dispensaryType: "",
    businessAddress: {
      street: "",
      city: "",
      state: "",
      zip: "",
    },
    firstName: "",
    lastName: "",
    contactNumber: "",
    email: "",
    password: "",
    agreeToTerms: false,
  });
  const [showValidation, setShowValidation] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);

  // Event handlers remain the same...
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const handleNestedChange = (section, field, value) => {
    setFormData((prev) => ({
      ...prev,
      [section]: { ...prev[section], [field]: value },
    }));
  };

  const handleNextStep = () => setStep((prev) => prev + 1);
  const handlePrevStep = () => setStep((prev) => prev - 1);

  const handleSubmit = async () => {
    try {
      setError(null); // Reset error state
      const response = await axios.post("/api/auth/register", formData);

      if (response.data.success) {
        setSuccess(true);
      }
    } catch (err) {
      setError(err.response?.data?.message || "Error registering vendor. Please try again.");
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4">{`Registration ${step}/3`}</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">Vendor registered successfully!</Alert>}

      {/* Render steps based on the current step */}
      {step === 1 && (
        <>
          <h5>Dispensary Details</h5>
          {/* Your form fields here... */}
        </>
      )}
      {step === 2 && (
        <>
          <h5>Business Owner Details</h5>
          {/* Your form fields here... */}
        </>
      )}
      {step === 3 && (
        <>
          <h5>Vendor Terms & Conditions</h5>
          {/* Terms and checkbox */}
        </>
      )}

      <div className="mt-4">
        {step > 1 && (
          <Button variant="secondary" onClick={handlePrevStep}>
            Back
          </Button>
        )}
        {step < 3 && (
          <Button
            variant="primary"
            onClick={() => {
              // Handle step validation
              handleNextStep();
            }}
          >
            Next
          </Button>
        )}
        {step === 3 && (
          <Button variant="success" onClick={handleSubmit} disabled={!formData.agreeToTerms}>
            Register
          </Button>
        )}
      </div>
    </div>
  );
};

export default VendorRegistration;
